INSERT INTO `recipe` (`id`, `name`, `ingredient`, `description`, `energy`, `carbohydrate`) VALUES
(1, 'Burgonyaleves', 'két személyre: 200 g burgonya, 1/2 fej vöröshagyma, petrezselyemzöld, zellerzöld, 1 dl joghurt, só.', 'Elkészítés: A burgonyát meghámozzuk, megmossuk, karikára vagy apró kockára vágjuk. Hozzáadjuk az apróra vágott hagymát, felöntjük 6dl vízzel és ízesítjük a fűszerekkel. Ha a burgonya megfőtt, behabarjuk a joghurttal, meghintjük zöldpetrezselyemmel.', 118, 22),
(1, 'Csontleves', 'két személyre: 400 g marhacsont, 100 g sárgarépa, 50 g fehérrépa, ½ fej vöröshagyma, ½ fej karalábé, ½ zöldpaprika, 2 gerezd fokhagyma, Vegeta, sáfrányos szeklice, petrezselyemzöld, ¼ zeller, só, bors', 'Elkészítés: A marhacsontot jól megmossuk, kb. másfél órát főzzük. Közben a sárgarépát, a zellert, a fehérrépát hosszában, a karalábét, a hagymát karikára vágjuk, és meghintve sáfrányos szeklicével, vegetával, petrezselyemzölddel teflonedényben pároljuk egy kevés vízben, lepirítjuk, majd hozzáadjuk a csontokhoz, és még kb. 30 percig együtt főzzük. Tálalás előtt leszűrjük.', 17, 4),
(1, 'Daragaluska', 'két személyre: 1 tojás, 4 ek. búzadara, egy csipet sütőpor', 'Elkészítés: Egy tojásfehérjéből kemény habot verünk, lazán hozzákeverjük a tojássárgáját, egy csipetnyi sütőport és kb. 4 ek. búzadarát, hogy gyenge tésztát kapjunk. A lassan forró levesbe szaggatjuk és 2-3 percig főzzük.', 105, 14),
(1, 'Gombaleves', 'két személyre: 100 g gomba, ½ fej vöröshagyma, 100 g sárgarépa, 50 g petrezselyemgyökér, só, fűszerpaprika, fehér bors, petrezselyemzöld, 1 tk. olaj.', 'Elkészítés: A megtisztított, megmosott zöldségeket szeletekre, a hagymát apróra vágjuk. Egy teflonfazékban olajon lepirítjuk az alapanyagokat, majd felengedjük 6 dl vízzel. Ízesítjük sóval, fehér borssal. Tálaláskor meghintjük az apróra vágott petrezselyemzölddel. Egy-egy adag: 261 kJ (62 kcal). 4 g szénhidrát. (Ha 1 dl joghurttal és 1 ek. liszttel behabarjuk: egy-egy adag: 468 kJ (109 kcal), 10 g szénhidrát.)', 62, 10),
(1, 'Gyümölcsleves', 'két személyre: 1 dl 12% zsírtartalmú tejföl, 1 el. liszt, pár szem szegfűszeg, darabos fahéj, ízlés szerint édesítőszer.', 'Elkészítés: Bármilyen gyümölcsből készíthetjük, vagy vegyes gyümölcsalapból, ekkor hozzá kell számítani a gyümölcs energia- és szénhidráttartalmát.', 85, 6),
(1, 'Almaleves', 'két személyre: 300 g alma, 1 dl 12% zsírtartalmú tejföl, 1 el. liszt, pár szem szegfűszeg, darabos fahéj, ízlés szerint édesítőszer.', 'Elkészítés: A megmosott gyümölcsöt felszeleteljük, és a fűszerekkel együtt kb. 6 dl vízben feltesszük főni. Tejfölből és lisztből habarást készítettünk, és ha a leves felforrt, behabarjuk. Melegen vagy hűtve, hidegen tálaljuk.', 130, 16),
(1, 'Meggyleves', 'két személyre: 200 g meggy, 1 dl 12% zsírtartalmú tejföl, 1 el. liszt, pár szem szegfűszeg, darabos fahéj, ízlés szerint édesítőszer.', 'Elkészítés: A megmosott gyümölcsöt felszeleteljük, és a fűszerekkel együtt kb. 6 dl vízben feltesszük főni. Tejfölből és lisztből habarást készítettünk, és ha a leves felforrt, behabarjuk. Melegen vagy hűtve, hidegen tálaljuk.', 135, 16),
(1, 'Cseresznyeleves', 'két személyre: 200 g cseresznye, 1 dl 12% zsírtartalmú tejföl, 1 el. liszt, pár szem szegfűszeg, darabos fahéj, ízlés szerint édesítőszer.', 'Elkészítés: A megmosott gyümölcsöt felszeleteljük, és a fűszerekkel együtt kb. 6 dl vízben feltesszük főni. Tejfölből és lisztből habarást készítettünk, és ha a leves felforrt, behabarjuk. Melegen vagy hűtve, hidegen tálaljuk.', 145, 19),
(1, 'Vegyes gyümölcsleves', 'két személyre: 100 g alma, 100 g szilva, 50 g meggy, 1 dl 12% zsírtartalmú tejföl, 1 el. liszt, pár szem szegfűszeg, darabos fahéj, ízlés szerint édesítőszer.', 'Elkészítés: A megmosott gyümölcsöt felszeleteljük, és a fűszerekkel együtt kb. 6 dl vízben feltesszük főni. Tejfölből és lisztből habarást készítettünk, és ha a leves felforrt, behabarjuk. Melegen vagy hűtve, hidegen tálaljuk.', 152, 21),
(1, 'Halászlé', 'két személyre: 300 g ponty, 1 fej vöröshagyma, 2 db zöldpaprika, 1 tk. fűszerpaprika, 1 tk. csípős fűszerpaprika, só', 'Elkészítés: A halat megtisztítjuk, feldaraboljuk, és főzés előtt fél órával besózzuk. A halszeleteket lábasba vagy bográcsba tesszük, ráöntünk 7 dl hideg vizet, és hozzáadjuk az apróra vágott hagymát és a zöldpaprikát. Amikor felforrt, beletesszük a fűszerpaprikát, és ízlés szerint sózzuk. A forrástól számított 20 percig főzzük.', 152, 21),
(1, 'Hideg paradicsomleves', 'két személyre: 500 g paradicsom, 2 dl kefir, só, fehér bors, petrezselyemzöld, metélőhagyma, ízlés szerint édesítőszer.', 'Elkészítés: A paradicsomot megtisztítjuk, megmossuk és apró kockára vágva nyersen összeturmixoljuk. Hozzákeverjük a kefirt, ízesítjük sóval, borssal és apróra vágott zöldfűszerekkel, esetleg édesítjük. Fél órára a hűtőbe tesszük.', 60, 10),
(1, 'Húsleves', 'két személyre: 100 g hús, 100 g sárgarépa, 50 g fehérrépa, ½ fej vöröshagyma, petrezselyemzöld, só, bors, ½ fej karalábé, ¼ fej zeller, ½ zöldpaprika, 2 gerezd fokhagyma.', 'Elkészítés: A húst 8 dl vízben feltesszük főni, lassú tűzön főzzük, s a keletkező habot leszedjük. Ezután tesszük bele a megtisztított és megmosott zöldségeket és a fűszereket. Lassú tűzön főzzük tovább. Leszűrve üres húslevesként is fogyasztható. Egy-egy adag: átlagosan: 400 kJ (95 kcal) 4 g szénhidrát. Ha 20 g levestésztát főzünk bele, akkor egy-egy adag 610 kJ (145 kcal), 11 g szénhidrát.', 95, 11),
(1, 'Karalábéleves', 'két személyre: 2 közepes karalábé (200 g), 100 g sárgarépa, 50 g fehérrépa, só, petrezselyemzöld, bors, 1 tk. olaj, vagy 1 tk. margarin.', 'Elkészítés: A megtisztított zöldségeket kockára vágjuk, teflonfazékban olajon lepirítjuk, majd 6 dl vízzel felengedjük. Ízesítjük sóval, borssal, apróra vágott petrezselyemzölddel. Egy-egy adag: 260 kJ (62 kcal) 4 g szénhidrát. Ha 1 dl joghurttal és 1 ek. liszttel behabarjuk, akkor egy adag 468 kJ (109 kcal), 10 g szénhidrát.', 62, 4),
(1, 'Karfiolleves', 'két személyre: 300 g karfiol, 100 g sárgarépa, 50 g fehérrépa, petrezselyemzöld, só.', 'Elkészítés: A zöldséget megtisztítjuk, megmossuk, a karfiolt rózsáira szedjük. A sárgarépát, a fehérrépát karikára vágjuk. A zöldségeket kb. 6 dl vízben főzzük, amíg a zöldségek megpuhulnak, majd ízlés szerint sózzuk, és tálaláskor meghintjük az apróra vágott petrezselyemzölddel. Egy-egy adag: 70 kJ (17 kcal), 4 g szénhidrát. Ha 1 dl joghurttal és 1 ek. lisztel behabarjuk, akkor egy-egy adag: 268 kJ (64 kcal), 10 g szénhidrát. Ha 20 g levestésztát főzzünk bele habarás helyett, akkor egy-egy adag', 17, 4),
(1, 'Májgaluska', 'két személyre: 1 csirkemáj, 1 tk. olaj, 2 tk. lereszelt vöröshagyma, ½ tojás, ½ tejbe áztatott zsemle, só, törött bors, petrezselyemzöld.', 'Elkészítés: Az olajon megpirítjuk a hagymát, majd a tűzről le véve hozzáadjuk a lekapart májat, a kinyomkodott, összetördelt zsemlét, a tojást, a fűszereket, és összekeverjük. A galuska keménységű tésztát egy órát állni hagyjuk, majd kanállal a levesbe szaggatjuk.', 144, 9),
(1, 'Palócleves', 'két személyre: 100 g marhahús. 80 g zöldbab, 100 g burgonya, 2 tk. olaj, 1 ek liszt, ½ dl tejföl, 1 kis fej vöröshagyma, 1 gerezd fokhagyma, fűszerpaprika, őrölt fekete bors, köménymag, 1 dl babérlevél, petrezselyemzöld, só.', 'Elkészítés: A megtisztított, apróra vágott hagymát az olajon átfuttatjuk, fűszerpaprikával meghintjük, és hozzáadjuk az apróra vágott húst. Megsózzuk, őrölt fekete borssal, köménymaggal és zúzott fokhagymával ízesítjük. Fedő alatt – időnként kevés vizet hozzá öntve – majdnem puhára pároljuk. Külön-külön enyhén sós vízben megfőzzük az egyenletes darabokra vágott zöldbabot és az apróra vágott burgonyát, majd a főzőlevükkel együtt a párolt húshoz adjuk. A levest összeforraljuk, és a tejfölből és li', 254, 14),
(1, 'Paradicsomleves', 'két személyre: 1 doboz paradicsompüré (50 g), 2 tk. liszt, só, bors, ½ zeller, ½ fej vöröshagyma, ízlés szerint édesítőszer.', 'Elkészítés: A paradicsompürét fel hígítjuk 6 dl vízzel. Teflonfazékban a lisztet szárazon kissé megpirítjuk, és felöntjük még a zellerrel és a hagymával, majd ezeket kivesszük és ízesítjük a levest sóval, borssal, esetleg édesítőszerrel. Egy-egy adag: 392 kJ (90 kcal), 9 g szénhidrát.', 90, 9),
(1, 'Sajtos zöldségleves', 'két személyre: 200 g sárgarépa, 50 g gyökér, 50 g karalábé, zellerlevél, zöldpetrezselyem, 1 tk. liszt, 1 tk. margarin, 2 ek. reszelt sajt, só.', 'Elkészítés: A vékony szálakra vágott vagy káposztareszelőn lereszelt zöldségeket megpároljuk a margarinon, hozzáadjuk az apróra vágott petrezselyemzöldet és a zellerzöldet, meghintjük a liszttel, átkeverjük és felöntjük kb. 6 dl. vízzel. Megsózzuk, majd addig főzzük, amíg a zöldségek meg nem puhulnak. Tálalás előtt keverjük bele a reszelt sajtot.', 135, 10),
(1, 'Sóskaleves', 'két személyre: 250 g sóska, 2 tk. olaj, 1 ek. liszt, 1 dl tej, ½ dl tejföl (12%-os), só, édesítőszer', 'Elkészítés: A Sóskát megtisztítjuk vastag ereitől és szárától, jól megmossuk, lecsepegtetjük. A felforrósodott olajra dobjuk és addig pároljuk fedő alatt, amíg péppé nem válik. Ekkor meghintjük a liszttel, átkeverjük, felöntjük hideg vízzel és tejjel. Sózzuk és édesítjük ízlés szerint. Tálalás előtt hozzámelegítjük, majd elkeverjük benne a tejfölt.', 124, 7),
(1, 'Zellerkrémleves', 'két személyre: 5 dl zöldséglé, 200 g tisztított zeller, 150 g burgonya, 4 ek. tejszín, őrölt fehér bors, só.', 'Elkészítés: A zöldséglevet egy levesfazékban felforraljuk és hozzáadjuk az apróra kockára vágott zellert és a burgonyát. Fedő alatt kb. 15 percig puhára főzzük. Ízesítjük sóval és borssal, ha lehűlt, a levével együtt leturmixoljuk. Visszaöntjük a fazékba, felforrósítjuk, és hozzámelegítjük a tejszínt. Apróra vágott zellerzölddel is tálalhatjuk.', 154, 17),
(1, 'Zöldbableves', 'két személyre: 300 g zöldbab, 100 g sárgarépa, 50 g fehérrépa, 1 db zöldpaprika, 2 gerezd fokhagyma, só, fűszerpaprika, ecet, petrezselyemzöld.', 'Elkészítés: A megtisztított zöldbabot 2cm-es darabokra, a sárgarépát és a fehérrépát karikákra vágjuk. Kevés vízzel fazékban pároljuk, hozzáadjuk a fokhagymát, fűszerezzük, majd felöntjük 6 dl vízzel. Tálaláskor meghintjük apróra vágott petrezselyemzölddel.', 70, 4),
(1, 'Zöldséges csirkeleves', 'két személyre: 150 g csirkemell vagy 200 g csirkeaprólék, 200 g mélyhűtött leveszöldség, 40 g kis kagylótészta, 7 dl zöldséglé, apróra vágott petrezselyemzöld és metélőhagyma, só őrölt bors.', 'Elkészítés: A megmosott csirkehúst egyforma csíkokra vágjuk, és a felhevített olajon átpirítjuk. Hozzáadjuk a zöldséget, ízesítjük sóval és borssal, ráöntjük a zöldséglevet és fedő alatt kb. 10-15 percig főzzük. Beletesszük a kagylótésztát és még kb. 10 percig főzzük. Az apróra vágott zöldfűszerekkel meghintve tálaljuk.', 206, 19),
(1, 'Zöldségleves', 'két személyre: 100 g sárgarépa, 50 g fehérrépa, 100 g karfiol, ½ fej karalábé. ½ zöldpaprika, petrezselyemzöld, 1 gerezd fokhagyma, Vegeta, bors, só.', 'Elkészítés: A megtisztított zöldségféléket karikára vágjuk, a zöldpaprikát felszeleteljük, és a teflonfazékban kevés vízzel az apróra vágott petrezselyemzölddel és vegetával megpároljuk, majd felengedjük 6 dl vízzel. Ízesítjük még fokhagymával. A zöldségeket puhára főzzük, esetleg tálalás előtt meghintjük még friss petrezselyemzölddel.', 17, 4),
(2, 'Gombamártás', 'két személyre: 200 g gomba, ½ fej vöröshagyma, ½ csomó petrezselyemzöld, só, bors, majoránna, 1 tk. olaj, 2 tk. liszt, csontlé, 1 dl 12%-os tejföl.', 'Elkészítés: Az olajon megpirítjuk a vékony szeletkere vágott gombát. Hozzáadjuk az apróra vágott petrezselyemzöldet és lereszelt hagymát. Sózzuk, borssal vagy majoránnával ízesítjük, megszórjuk liszttel, átpirítjuk és felöntjük vízzel vagy csontlével. Tálaláskor keverjük bele a tejfölt.', 136, 5),
(2, 'Gyümölcsmártás', 'két személyre: 3 tk. liszt, 1 dl 12%-os tejföl, fahéj, citromkarikák, só, édesítőszer', 'Elkészítés: 400 g tetszőleges gyümölcsöt tegyünk fel a már fűszerekkel ízesített aromás főzetbe főni. Amikor megpuhult, behabarjuk és ízlés szerint adunk hozzá édesítőszert, az egészet átforraljuk, és késhegynyi sóval ízesítjük.', 93, 7),
(2, 'Kapormártás', 'két személyre: 1 csomó friss kapor vagy 2-4 g szárított kapor, 2 tk- margarin, 1 ek. liszt, 2 dl 1,5%-os tej, 0,5 dl 12%-os tejföl, citromlé, só.', 'Elkészítés: Egy lábasban felhevítjük a margarint, majd egyszerre ráhintjük a lisztet és az apróra vágott kaprot. Lassú tűzön többször átkevergetjük, hogy ne barnuljon meg. Hideg vízzel felöntjük, simára keverjük. Ízesítjük citromlével, sóval, még 5 percig felforraljuk, majd tejjel felengedjük. Tálalás előtt hozzákeverjük a tejfölt.', 140, 10),
(2, 'Paradicsommártás', 'két személyre: 400 g friss paradicsom, vagy 1 dobot paradicsompüré, 1 ek. liszt, 2 tk. olaj, só, édesítőszer.', 'Elkészítés: Az olajból és a lisztből világos rántást készítünk, és a kb. 5 dl-re felengedett paradicsompürével öntjük fel, sózzuk, édesítjük, és még kb. 4 percig főzzük.', 156, 12),
(2, 'Sajtmártás', 'két személyre: 80 g reszelt sajt, 3 dl 1,5%-os tej, 10 g margarin, 2 ek. liszt, só, reszelt szerecsendió.', 'Elkészítés: A felhevített margarinon zsemleszínűre pirítjuk a lisztet, majd óvatosan, lassan hozzáöntjük a tejet, és krémszínűre főzzük. Ízesítjük sóval, szerecsendióval. Végük hozzáadjuk a reszelt sajtot, és még egyszer összefőzzük.', 204, 15),
(2, 'Sóskamártás', 'két személyre: 500 g friss sóska, 10 g margarin, 2 ek. liszt, 1 dl 12%-os tejföl, 2 dl 1,5%-os tej, só, édesítőszer.', 'Elkészítés: A megmosott, szárától megtisztított sóskát a margarinon zsemleszínűre pirított liszthez hozzáadjuk. Folytonos keverés mellett teljesen finomra fonnyasztjuk, kb. 2 dl vízzel vagy csontlével felengedjük, és tálalás előtt hozzámelegítjük a tejfölt. Ízesítjük sóval és mesterséges édesítőszerrel.', 168, 14),
(3, 'Diétás majonéz', 'két személyre: 3 ek. félzsíros tehéntúró, 1 tojássárgája, 2 tk. olaj, 1 mk. mustár, citromlé, só, őrölt fehér bors.', 'Elkészítés: A szitán áttört túrót elkeverjük a tojássárgájával, az olajjal, a mustárral, majd ízesítjük citromlével, sóval, őrölt fehér borssal.', 115, 2),
(3, 'Gyümölcsös túrókrém', '150 g félzsíros tehéntúró, 1 tojás, reszelt citrom- vagy narancshéj, édesítőszer.', 'Elkészítés: A túrót a tojássárgájával krémesre keverjük, ízesítjük a reszelt citromhéjjal, az édesítővel, majd lazán hozzákeverjük a felvert fehérje habját. Két talpas poharat vagy kis kompótos tálat félig megtöltünk a túrókrémmel, ráhalmozzuk a szezonnak megfelelő, megmosott, megtisztított, ha kell kimagozott gyümölcsöt. 1-2 szemet, szeletet vagy darabot mindig hagyjunk a díszítésre. A gyümölcsöt fedjük be a maradék túrókrémmel, és díszítsük. Egy főre kb. 50 g gyümölcsöt vegyünk.', 145, 6),
(3, 'Gyümölcsturmix', '5 dl 1,5%-os tej, citromlé, édesítőszer.', 'Elkészítés: A megtisztított, megmosott, feldarabolt gyümölcsöt tegyük a turmixgépbe, adjunk hozzá tejet, a citromlevet és az édesítőszert ízlés szerint', 142, 19),
(3, 'Joghurtos eper kivivel', '2 dl joghurt, 1 dl tejszín, 300 g eper, 100 g kivi, édesítőszer.', 'Elkészítés: A tejszínhabot felverjük, és egy tálban hoz keverjük a joghurtot. Az epret megtisztítjuk, szűrőkanálban csap alá tartva megmossuk, négy szemet a díszítés hez félreteszünk, a többit szeletekre vágjuk, és a joghurt hoz keverjük. A kivit meghámozzuk és felszeleteljük. A joghurtos epret gyümölcsös poharakba szétosztjuk, tetőjükre rakjuk a kiviszeleteket, és még két-két szem eperrel díszítjük.', 231, 22),
(3, 'Joghurtos sajtmártás', 'két személyre: 2 dl joghurt, 1 tk. mustár, 5 ek, reszelt trappista sajt, petrezselyemzöld, késhegynyi őrölt szerecsendió, só.', 'Elkészítés: Az alapanyagokat jól elkeverjük, és legalább a kétszer fél óráig hagyjuk állni a hűtőben, hogy az íze összeérjen.', 150, 5),
(3, 'körözött', 'két személyre: 40 g juhtúró, 40 g félzsíros tehéntúró, 1 ek. 12%-os tejföl, 1 kis fej vöröshagyma, őrölt köménymag, fűszerpaprika, só.', 'Elkészítés: A juhtúróhoz és a tehéntúróhoz keverjük a tejfölt, a megtisztított apróra reszelt hagymát, az őrölt köménymagot, a fűszerpaprikát és a sót. Lehűtve tálaljuk', 92, 0),
(3, 'Párizsikrém', 'két személyre: 60 g darált párizsi, 2 tk. Delma light margarin, 1 ek. kefir, petrezselyemzöld, 1 tk, apróra vágott vöröshagyma, 1 tk, mustár.', 'Elkészítés: Egy kis tálban a ledarált párizsihoz adjuk a margarint, a kefirt, a reszelt vöröshagymát, a finomra vágott petrezselyemzöldet, a mustárt, és az egészet jól össze- keverjük. Kenyérre kenve tálalhatjuk, vagy melegszendvics alapanyagaként használhatjuk.', 105, 0),
(3, 'Sajtkrém', 'két személyre: 60 g óvári sajt, 1 tk. Delma light, 1 ek. kefir, metélőhagyma, szerecsendió, só.', 'Elkészítés: A lereszelt sajthoz keverjük a margarint és a kefirt, majd ízesítjük a finomra vágott metélőhagymával, a reszelt szerecsendióval és a sóval. Lehűtve tálaljuk.', 118, 0),
(3, ' Sajtos almaszendvics', 'két személyre: 2 közepes alma (kb. 200 g). 8 ek. reszelt óvári sajt, 4 ek. joghurt.', 'Elkészítés: A megmosott alma közepét körbe vágva, magházát kiemeljük. Az 1-2 cm-es szeletekre vágott almát megkenjük a joghurttal elkevert reszelt sajttal. Egymásra rakva a szeleteket, Folpackba csomagolva, a munkahelyünkre is vihetjük magunkkal tízóraira.', 185, 10),
(3, 'Sárgadinnye sonkával', 'két személyre: 4 szelet (kb. 400 g) sárgadinnye, 4 vékony szelet gépsonka (kb. 40 g)', 'Elkészítés: A dinnyegerezdeket betekerjük a gépsonkaszeletekkel. Jól lehűtve fogyasztjuk.', 110, 20),
(3, 'Tavaszi Krém', 'két személyre: 6 ek. reszelt óvári sajt, 2 ek. félzsíros tehéntúró, 2 tk. Delma margarin, 2 ek. joghurt, petrezselyemzöld, metélőhagyma.', 'Elkészítés: Az alapanyagokat kenhetővé keverjük, és ízesítjük az apróra vágott fűszerekkel.', 175, 2),
(3, 'Tojáskrém', 'két személyre: 1 főtt tojás, 2 tk. Delma light margarin, 2 ek. kefir, petrezselyemzöld, tk. mustár bazsalikom, őrölt fekete bors, só.', 'Elkészítés: A margarint a reszelt főtt tojással és a kefirrel habosra keverjük, és ízesítjük a finomra vágott petrezselyem. Milyen Zölddel, a mustárral, a bazsalikommal a fekete borssal és a sóval. Lehűtve szendvicskrémnek fogyaszthatjuk.', 75, 1),
(3, 'Tojással töltött uborka', 'két személyre: 400 g kígyóuborka főtt tojás, 1 ek. tejszín, 1 tk. apróra vágott kapor.', 'Elkészítés: A kettévágott uborka belsejét kikaparjuk, és összekeverjük az apróra vágott főtt tojásokkal, a tejszínnel, a kaporral. A keveréket visszatöltjük az uborkába. Fél órára hűtőbe tesszük.', 97, 0),
(3, 'Töltött paradicsom', 'két személyre: 400 g paradicsom, 50 g májkrém, 2 főtt tojás, apróra vágott petrezselyemzöld, só.', 'Elkészítés: A paradicsomot félbevágjuk, kimagozzuk. megsózzuk. A májkrémből, az apróra vágott főtt tojásokból, a petrezselyemzöldből masszát készítünk, hozzáadjuk kivájt paradicsomrészeket, és betöltjük a paradicsomokba. Lehűtve tálaljuk.', 140, 2),
(3, 'Túróval töltött zöldpaprika', 'Hozzávalók két személyre: 4 közepes zöldpaprika (400 g), 3 ek. juhtúró, 3 ek. félzsíros tehéntúró, 2 ek juhtúró, fűszer paprika.', 'Elkészítés: A kétféle túrót elkeverjük a joghurttal, a paprikával, és a kétfelé vágott, kimagozott paprika belsejébe töltjük', 202, 5),
(3, 'Vegetáriánus májkrém', 'Hozzávalók két személyre: 100 g lencse, 3 dl zöldséglé, 150 g vöröshagyma (egy nagyobb és egy kisebb). 2 db babérlevél, 3-4 szegfűszeg, 2 szál újhagyma, 1 gerezd fokhagyma, 2 t. olaj, 1/2 citrom reszelt héja, rozmaring, fűszeres só, frissen őrölt fekete bors (vagy borsikafű), szerecsendió, 2 tk. szójaszósz, 1 ek. majoránna.', 'Elkészítés: A lencsét előző este a zöldséglébe beáztatjuk, A kisebbik hagymát megtűzdeljük a babérlevéllel és a szegfűszeggel. A megtisztított újhagymát karikákra vágjuk. A lencsét a zöldséglében a tűzdelt vöröshagymával és az újhagymával együtt puhára főzzük. Közben a felhevített olajon a maradék apróra vágott hagymát és fokhagyma már a reszelt citromhéjjal és a rozmaringgal együtt meg pirítjuk. Ha a lencse kihűlt, a turmixgépbe öntjük (a tűzdelt hagymát eltávolítjuk) hozzákeverjük a pirított fűszereket, és ízesítjük még a fűszeres sóval, a fekete borssal. a szójaszósszal és a majoránnával. Leturmixoljuk, és ha kell, még ízlés szerint fűszerezzük. Különösen finom apróra vágott savanyú uborka kockákkal. A pástétomot kenyérre kenve fogyaszthatjuk. Négy részre oszthatjuk', 125, 13),
(4, 'Burgonyakrokett', 'Hozzávalók két személyre: 2 közepes burgonya (200 g), 1 ek. liszt, 1/2 tojás, petrezselyemzöld, só, bors, 2 ek. olaj.', 'Elkészítés: A héjában főtt burgonyát meghámozzuk, át R törjük, hűlni hagyjuk. Hozzáadjuk a lisztet, a felvert tojást, sóval, borssal, petrezselyemzölddel ízesítjük. Jól for málható tésztát készítünk belőle, négyfelé osztjuk, és kb. 4-5 cm hosszú, 1,5 cm széles rudacskákat formálunk belőle. (Ha kell, még kevés lisztet használhatunk a formázás hoz.) Forró olajban, a lángot fokozatosan mérsékelve, hogy a belseje is átsüljön, ropogósra sütjük.', 230, 23),
(4, 'Burgundi marhasült', 'két személyre: 300 g (4 vékony szelet) marhacomb, 100 g vegyes zöldség, (sárgarépa, gyökér). 1/2 fej vöröshagyma, 1 kis gerezd fokhagyma, egész bors, kakukkfű, 1 db babérlevél, 1 ek. paradicsompüré, 1 dl vörösbor, só, 50 g gomba, 1 tk. olaj. I ek, liszt', 'Elkészítés: Az előre lesózott marhaszeleteket teflon edényben átsütjük. (Az edényt tollal, vékonyan olajjal kikenjük.) A húsokat sütés után félretesszük. A vágott zöldéget, a hagyma egy részét (3 dkg), a fokhagymát kevés víz hozzáadásával (a zöldséget ellepje) megpuhítja adjuk a paradicsompürét, a babérlevelet, a kakukk vet, a borsot, a vörösbort és az 1 ek. liszttel beint Összekeverjük. majd felöntjük 1/2-1 dl vízzel, és felforraljuk. Beletesszük a hússzeleteket, addig pároljuk, amíg a hús meg nem puhul. Ekkor kivesszük a hússzeleteket. és a mártást átszűrjük. (Ha túl híg, a fölösleges vizet elpárologtatjuk.) A maradék hagymát teflonban fonnyasztjuk, s erre tesszük az 50 g apróra vágott gombát, sózzuk a vés vízzel pároljuk, amíg a gomba megpuhul. Amiker készült, a gombát a fenti mártásba keverjük. A mártást forrón a hússzeletekre öntjük.', 274, 7),
(4, 'Csirkegombóc', 'két személyre: 200 g főtt csirkehús, I tojás, petrezselyemzöld, só, 2 ek. zabpehely', 'Elkészítés: A csirkehúst bőröstől megdaráljuk, elkeverd a tojássárgájával, apróra vágott petrezselyemzölddel Hozzáadjuk a zabpelyhet, és a felvert tojás habját könnyedén belekeverjük. 4 gombócot formázunk. Forrásban lévő sós vízben addig főzzük a gombócokat, amig fel nem jönnek a víz tetejére, majd leszűrjük.', 195, 7),
(4, 'Csirkepörkölt', 'két személyre: 300 g csirkeaprólék, 1 közepes vöröshagyma, 1 közepes zöldpaprika 1 közepes paradicsom, só. 1 mk. fűszerpaprika.', 'Díszítés: Teflon edényben az apróra vágott hagymát kevés vízzel megpároljuk, hozzáadjuk a jól megmosott csirkeaprólékot, sózzuk, megszórjuk fűszer paprikával, majd felöntjük vízzel úgy, hogy a húst éppen elfedje. Mikor felforrt, beleszeljük a megmosott, kimagozott zöldpaprikát, valamint a paradicsomot. Teljesen puhára főzzük.', 110, 0),
(4, 'Fasírozott római tálban', 'két személyre: 200 g darált sovány sertéshús, 1 főtt és I nyers tojás, fűszer paprika, petrezselyemzöld, 1 gerezd fokhagyma, majoránna, só.', 'Elkészítés: A darált húsra ráreszeljük a főtt tojást, ráütjük a nyers tojást, majd jól összedolgozzuk a fűszerekkel. A masszából hosszúkás cipót formázunk. Beletesszük a ró- mai tálba. Kb. 50 percig sütjük. Az utolsó 10 percben a római tál fedelét levévé hagyjuk pirosra sülni.', 215, 0),
(4, 'Gombakrokett', 'Hozzávalók két személyre: 250 g gomba. 1/2 fej vöröshagyma, 150 g darált hús. I főtt tojás, I nyers tojás, I ek búzakorpa, 1 tk. mustár, bors. majoránna, só, petrezselyemzöld.', 'Elkészítés: A megtisztított vöröshagymát, gombát apróra vágjuk, teflonedényben megpároljuk, hozzáadjuk a darált húst és az összevágott petrezselyemzöldet. Ha kihűlt, be- lereszeljük a főtt tojást, összedolgozzuk a búzakorpával, majd a fűszerekkel ízesítjük, és a nyers tojás hozzáadásával masszát készíteni. Őzgerinc sütőt alufóliával kibéleljük, belehelyezzük a masszát, és közepesen forró tepsiben lassan sütjük. Majd fóliával együtt kivesszük az őzgerinc formából és 10 perc múlva felszeleteljük (kb. 8 szelet). 1 adag két szelet.', 180, 3),
(4, 'Gombapörkölt', 'két személyre: 400 g gomba, 1 kisebb vöröshagyma, 1 csapott ek, fűszerpaprika, 1 csapott ek. paradicsompüré, só bors.', 'Elkészítés: A megtisztított, szeletekre vágott gombát az apróra vágott hagymával együtt teflon edényben lepirítjuk, majd felöntjük annyi vízzel, hogy a gombát elfedje. Ha forr, ízesítjük a fűszerpaprikával, a paradicsompüré vel, a sóval, a borssal. Fedő alatt addig pároljuk, amíg puha nem lesz. Egy-egy adag: 210 kJ (50 kcal), 2 g szénhidrát.\r\nHa 2 tk. olajjal készítjük, akkor egy-egy adag: 400 kJ (95 kcal), 2 g szénhidrát.', 50, 2),
(4, 'Lecsós marhaszelet', 'két személyre: 200 g sovány marhahús, 12 fej vöröshagyma, két közepes paradicsom 3 közepes zöldpaprika, só, bors, piros fűszerpaprika.', 'Elkészítés: A megtisztított hagymát karikára, a megmosott zöldpaprikát szeletekre vágjuk, megsózzuk, a teflon- lábasban szárazon pirítjuk, megszórjuk kb. evőkanálnyi fűszerpaprikával, majd felöntjük még 1 dl vízzel, és mikor felforrt, beletesszük a két szelet marhahúst. Addig pároljuk, amíg a hús meg nem puhul. Télen 30 dkg lecsóból készíthetjük.', 120, 0),
(4, 'Marhapörkölt', 'két személyre: 200 g sovány marhahús, 1 fej vöröshagyma, 1 zöldpaprika, só, fűszerpaprika.', 'Elkészítés: Először a megtisztított, kockára vágott hagymát teflonedényben kevés vízben puhára főzzük, megszórjuk kevés fűszerpaprikával, és mindjárt hozzáadjuk a 2x2 cm-es darabokra vágott húst. A húst kevergetve minden oldalról jól átsütjük, és felengedjük annyi vízzel, hogy jól ellepje. Ízesítésként adunk hozzá zöldpaprikát, paradicsomot, megsózzuk. Jól lefedve addig pároljuk, amig híg levét el nem fövi és puha nem lesz. Egy-egy adag: 588 kJ (140 kcal), 0 g szénhidrát.\r\nHa a vöröshagymát 1 ek. olajon fonnyasztjuk, akkor egy adag: 735 kJ (175 kcal).', 140, 0),
(4, 'Natúr sertésborda', 'két személyre: 2 szelet (160 g) sertésborda, 1 ek. olaj, 2 ek. liszt, só.', 'Elkészítés: A hússzeleteket húsverővel kissé kiverjük, sózzuk. Az olajjal kikent teflon edényben a lisztben megforgatott szeleteket hirtelen pirosra sütjük.', 190, 7),
(4, 'Párolt húsok olaj nélkül', 'két személyre: 200 g sertés vagy csirkehús, 1 közepes nagyságú vöröshagyma, só.', 'Elkészítés: Teflonedénybe vékony karikákra szeletelünk közepes nagyságú hagymát, és szárazon pirítjuk. Hozzáadjuk a kiveréssel fellazított, besózott hússzeleteket, és mindig kevés vizet hozzáöntve puhára pároljuk. Egy-egy adag: sertéshúsból: 630 kJ (150 kcal), 0 g szénhidrát. csirkehúsból: 460 kJ (110 kcal), 0 g szénhidrát.', 150, 0),
(4, 'Rántott szelet, csirke vagy hal', 'két személyre: 160 g színhús (csirke, hal). 2 tk, liszt, 1/2 tojás, 4 tk, zsemlemorzsa, 1 dl olaj, só.', 'Elkészítés: A húst, a szeleteket a hártyától megtisztítjuk, besózzuk, veréssel fellazítjuk. 10 percig állni hagyjuk. majd bele mártjuk lisztbe, felvert tojásba és zsemlemorzsába, majd bő, forró olajban ropogósra sütjük.', 239, 10),
(4, 'Reszelt máj', 'két személyre: 150 g máj, 1 ek. olaj, I fej vöröshagyma, fűszerpaprika, 1/2 zöldpaprika, 1 db paradicsom.', 'Elkészítés: A megmosott májat ereitől megtisztítjuk, egy forma csíkokra vágjuk. A felhevített olajon az apróra vágott vöröshagymát megfonnyasztjuk, megszórjuk a fűszer paprikával és hozzáadjuk a karikákra vágott zöldpaprikát cikkekre vágott paradicsomot. Beletesszük a májat, és fedő alatt puhára pároljuk. Amikor elkészült, megsózzuk.', 144, 3),
(4, 'Sertésborda gombával', 'Hozzávalók két személyre: 200 g (2 szelet) karaj, 100 g gomba, 1/2 dl fehér bor, 1 ek. paradicsompüré, 2 ek. liszt, 1 tk. olaj, 1/2 fej vöröshagyma, bors, só, petrezselyemzöld, 1 tk. margarin.', 'Elkészítés: A karajszeleteket kiverjük, sózzuk, lisztben megforgatjuk, s a teflonban felhevített olajban mindkét oldalukat pirosra sütjük. A szeleteket tálra helyezzük. Ugyanebben a teflon edényben a paradicsompürét, a fehér bort és kb. 1 dl vizet néhány percig forraljuk. Ráöntjük a bordákra, és befedve puhára pároljuk. Az apróra vágott vöröshagymát teflonban a margarinon sárgára pi rítjuk, hozzáadjuk a finomra vágott petrezselymet és a jól megmosott, szeletekre vágott gombát. Megsózzuk, és 4-5 percig pirítjuk. A puha bordákhoz hozzáadjuk a gombát, s készre pároljuk. A mártás se túl sűrű, se túl híg ne legyen!', 244, 99),
(4, 'Sült hús', 'két személyre: 200 g sertéshús, 1 ek, olaj, só.', 'Elkészítés: Az olajban a megmosott, konyhapapírral letörölgetett húst hirtelen átforgatjuk, hogy pörzsanyag kézen, és sütés közben saját levével időnként meglocsoljuk, hogy ne száradjon ki és ne égjen le. Természetesen a sütéshez felhasználhatunk vöröshagymát, zöldpaprikát. paradicsomot vagy lecsót is. Ha a hús megsült, nem vágjuk fel azonnal. 10 percig lefedve meleg helyen pihentetjük. Rostjait keresztbe vágva szeleteljük.', 150, 0),
(4, 'Tengerihal-szeletek sütve', 'két személyre: 400 g mélyhűtött tengeri hal, 1/2 citrom leve, só, őrölt fehér bors, 50 g Héra margarin', 'Elkészítés: A mélyhűtött halat hagyjuk egy tálban kiolvadni, leöntjük a levet róla, majd besózzuk, meghintjük őrölt fehér borssal és meglocsoljuk a kicsavart citrom levével. Néhány percig hagyjuk állni. A halszeleteket le csöpögtetjük, szárazra törölgetjük konyhapapírral. Felhevítjük egy serpenyőben a margarint, és a halszeletek mindkét oldalát kb. 3-3 percig sütjük. Vékony busaszeletekkel is elkészíthetjük', 260, 0),
(4, 'Tojásropogós', 'két személyre: 2 főtt tojás, 1/2 nyers tojás, 1 zsemle, petrezselyemzöld, só, 2 ek. olaj.', 'Elkészítés: A főtt tojásokat lereszeljük, a zsemlét hideg vízbe beáztatjuk. Kicsavarjuk, majd főzőkanállal szét nyomkodjuk. Hozzáadjuk a 1/2 nyers tojást, a lereszelt tojásokat és az apróra vágott petrezselymet, sózzuk, és rugalmas masszát készítünk. Négyfelé osztjuk, kis rudacskákat formálunk belőle, felhevített olajban kisütjük.', 260, 15),
(4, 'Zabpehely vagdalt pogácsák', 'két személyre: 120 g zabpehely, 2.5 dl víz 1/2 fej vöröshagyma, 1 db babérlevél, 1/2 db tojás 2 ek. zsemlemorzsa, fűszerpaprika, őrölt fehér bors 3 ek, olaj a kisütéshez, só.', 'Elkészítés: Egy fazékban a megadott vízmennyiséghez hozzáadjuk a zabpelyhet, a megtisztított és apróra vágott hagymát, a sót és a babérlevelet. Felforraljuk, a tűzhelyet kikapcsoljuk, s fedő alatt hagyjuk megpuhulni. Közben néhányszor megkevergetjük. Ha megpuhult, a babérlevelet ki vesszük és hagyjuk kihűlni. Hozzáadjuk a tojást. a zsemlemorzsát, és ízesítjük a fűszerpaprikával és az őrölt fehér borssal. Ha nem akar összeállni a massza, akkor esetleg még tojást vagy zsemlemorzsát keverhetünk hozzá. Vizes kézzel négy egyforma lapos pogácsát formázunk, és a felhevített olajban mindkét oldalukat ropogósra sütjük. Kevés olajat vesz fel.', 168, 23),
(5, 'Burgonyafőzelék', 'két személyre: 400 g burgonya, 1 ek. olaj, 1/2 fej vöröshagyma, 1 dl 12%-os tejföl, 1 ek. liszt, só, babérlevél, fűszerpaprika.', 'Elkészítés: Az olajon világosbarnára sütjük a megtisztított, lereszelt hagymát, meghintjük fűszerpaprikával, majd hozzáadjuk a karikára vagy kockára vágott burgonyat. Kevés vizet hozzáöntve, babérlevéllel, sóval ízesítve fedő alatt puhára pároljuk. A tejföllel simára kevert liszt- behabarjuk, még l-2 percig forraljuk.', 300, 45),
(5, 'Karalábéfőzelék', 'két személyre: 400 g burgonya, 1 ek. olaj, 1/2 fej vöröshagyma, 1 dl 12%-os tejföl, 1 ek. liszt, só, babérlevél, fűszerpaprika.', 'Elkészítés: Az olajon világosbarnára sütjük a megtisztított, lereszelt hagymát, meghintjük fűszerpaprikával, majd hozzáadjuk a karikára vagy kockára vágott burgonyat. Kevés vizet hozzáöntve, babérlevéllel, sóval ízesítve fedő alatt puhára pároljuk. A tejföllel simára kevert liszt- behabarjuk, még l-2 percig forraljuk.', 300, 45),
(5, 'Kelkáposzta-főzelék', 'két személyre: 500 g kelkáposzta, 1 ek. liszt, 1 közepes burgonya, 1 mk. köménymag, só, fokhagyma, 2 dl 1,5%-os tej.', 'Elkészítés: A kelkáposzta megmosott, világos, kb., maroknyi mennyiségű levelét félretesszük, a többit a torzsájával együtt vékony csíkokra szeleteljük. Megsózva, köménymaggal, fokhagymával ízesítve, a meghámozott, kockára vágott burgonyával kb. 2,5 dl vízben kuktában 20 percig főzzük, a kukta fedelét levesszük, a kelkáposztához hozzáöntjük az 1-2 dl tejjel elkevert lisztet, átforraljuk. Tálaláskor apróra vágott friss levelekkel szórjuk meg.', 129, 6),
(5, 'Lencsefőzelék', 'két személyre: 120 g lencse, 1 ek. olaj, 2 ek. liszt, 1 kis fej vöröshagyma, egy kis darab citromhéj, 1 db babérlevél, fűszerpaprika, 1 dl tejföl só.', 'Elkészítés: A megtisztított lencsét többször megmossuk, egy éjszakára hideg vízbe áztatjuk, a vizet leöntjük, és másnap a lencsét sóval, citromhéjjal és babérlevéllel ízesítve kuktában megfőzzük. Az olajból és a lisztből zsemle színű rántást készítünk, és hozzáadjuk a fűszerpaprikát és az apróra vágott hagymát. A rántással besűrítjük a főzeléket, és tálaláskor ízesítjük a tejföllel.', 246, 41),
(5, 'Paradicsomos káposztafőzelék', 'két személyre: 500 g fejeskáposzta, 1 ek, olaj, 2 ek. liszt, 50 g paradicsompüré, 1/2 fej vöröshagyma, folyékony mesterséges édesítőszer, só.', 'Elkészítés: A káposztát ujjnyi vastagra szeleteljük, és az apróra vágott hagymával sós vízben puhára főzzük. Zsemleszínű rántást készítünk, ezt felengedjük a paradicsomlével, hozzáadjuk a leszűrt káposztát, ha kell, főzőlevével még hígítjuk. 10 percig főzzük tovább, mesterséges édes tószerrel ízesítjük, sózzuk.', 107, 17),
(5, 'Parajfőzelék', 'két személyre: 500 g paraj, 2 dl 1,5%-os tej, 15 g Héra margarin, 2 ek. liszt, őrölt fekete bors, fokhagyma, só.', 'Elkészítés: A megtisztított parajt sós vízben puhára főzzük. Leszűrjük, szitán áttörjük vagy turmixgépben leturmixoljuk. Világos rántást készítünk, ehhez adjuk a parajt, felengedjük a tejjel. Ízlés szerint fokhagymával, fekete borssal ízesítjük. Jól felfőzzük.', 125, 12),
(5, 'Parajfőzelék zsírszegényen', 'két személyre: 500 g paraj, 1,5 dl 1,5%-os tej 1 tojás, só, bors, 2 gerezd zúzott fokhagyma.', 'Elkészítés: A parajt sós vízben előfőzzük, leszűrjük, összevágjuk, a tejjel felengedjük. Sóval, zúzott fokhagymával ízesítjük. Forrás után belecsurgatunk a főzelékbe egy jól felvert tojást.', 100, 7),
(5, 'Salátafőzelék', 'két személyre: 2 fej fejes saláta, 1 ek, olaj, 1 ek. liszt, 1 dl 12%-os tejföl, kapor, fokhagyma, ecet, só, Polisweet folyékony édesítőszer.', 'Elkészítés: A megtisztított salátát sós vízben puhára főzzük. Világos rántással berántjuk, apróra vágott fokhagymával, kaporral, ecettel, folyékony mesterséges édesítő szerrel ízesítjük. Legvégül hozzákeverjük a tejfölt és még egyszer felforraljuk.', 129, 6),
(5, 'Sárgarépa-főzelék', 'két személyre: 500 g sárgarépa, 15 g Héra margarin, 1 ek. liszt, petrezselyemzöld, só, csontlé. 1,5 dl 1,5%-os tej', 'Elkészítés: A megtisztított sárgarépát lereszeljük, és a margarinon kevés csontlével, megsózva, fedő alatt puhára pároljuk. Zsírjára pirítjuk, a liszttel meghintjük, tejjel felengedjük, megszórjuk petrezselyemzölddel. Még néhány percig forraljuk.', 190, 27),
(5, 'Sóskafőzelék', 'két személyre: 500 g sóska, 2 tk, olaj, 1 ek. liszt, 2 dl 1,5%-os tej, 1 dl 12%-os tejföl, Polisett mesterséges édesítőszer, só.', 'Elkészítés: A sóskát vastag ereitől és száraitól megtisztítjuk, jól megmossuk, csurgatjuk. A felhevített olajon addig pároljuk, amíg szét nem fő. Meghintjük liszttel, tejjel felengedjük, ha kell, leturmixolhatjuk. Mesterséges édes tószerrel, sóval, tejföllel ízesítjük.', 172, 10),
(5, 'Szárazbabfőzelék', 'két személyre: 140 g fehér, vagy tarka bab, 1 ek. olaj, 2 ek. liszt, 1 kis fej vöröshagyma, I gerezd fokhagyma, 1 dl tejföl, fűszerpaprika, ecet, só, babérlevél.', 'Elkészítés: A megtisztított száraz babot este beáztatjuk, és másnap kuktában megfőzzük. Az olajból és a lisztből zsemleszínű rántást készítünk, és hozzákeverjük az apróra vágott hagymát, a fokhagymát és a fűszerpaprikát. A megfőtt bab hoz adjuk a rántást, ízesítjük az ecettel és a tej- föllel.', 339, 46),
(5, 'Tökfőzelék', 'két személyre: 500g gyalult tök, 1 ek. olaj, 1/2 fej vöröshagyma, 1 dl 12%-os tejföl, 1 ek. liszt, só, fűszer paprika, fél csomó kapor, ecet.', 'Elkészítés: Az olajon megpirított, lereszelt hagymát meg. hintjük a fűszerpaprikával, sózzuk, majd hozzáadjuk a tököt. A tököt saját levében puhára pároljuk, az apróra vágott kaporral ízesítjük, a liszttel simára kevert tejföllel be- habarjuk, és még kevés ecetet csepegtethetünk hozzá.', 129, 5),
(5, 'Zöldbabfőzelék', 'két személyre: 400 g zöldbab, 1 ek. liszt, 1 fej vöröshagyma, 1/2 gerezd fokhagyma, 1 dl joghurt, petrezselyemzöld, ecet, só.', 'Elkészítés: A megtisztított és felvágott zöldbabot sós vízben puhára főzzük. A víz éppen, hogy elfedje. Teflonedényben kissé megfonnyasztjuk az apróra vágott vöröshagymát, és a kés lapjával péppé nyomott fokhagymával együtt a puhára főtt zöldbabhoz adjuk. A főzeléket a joghurttal elkevert liszttel behabarjuk. Még 5 percig főzzük. Levesszük a tűzről, és még ízesítjük kevés ecettel. Tálalás előtt bele keverjük az apróra vágott petrezselyemzöldet.', 50, 6),
(5, 'Zöldborsófőzelék', 'két személyre: 500 g zöldborsó, 15 g Héra margarin, 1 ek. liszt, 0,5 dl tej, petrezselyemzöld, só.', 'Elkészítés: A kifejtett, megmosott vagy mélyhűtött zöld borsót a margarinon puhára pároljuk. Megszórjuk az apróra vágott petrezselyemmel, meghintjük a liszttel, sózzuk, elkeverjük. Előbb kevés vízzel, majd tejjel felengedjük. Még kb. 5 percig főzzük.', 300, 40),
(6, 'Babsaláta krinolinnal', 'két személyre: 80 g tarka bab, 80 g fehér bab, 1 nagy fej vöröshagyma, 150 g krinolin, 1 gerezd fokhagyma, 2 ek. ecet, 1 ek. olaj, őrölt fehér bors, só.', 'Elkészítés: A száraz babot előző este beáztatjuk. A levét másnap leöntjük, és a babot enyhén sós vízben kuktában puhára főzzük, leszúrjuk, hagyjuk lecsöpögni. A megtisztított hagymát kockára, a fokhagymát apróra vágjuk. Az krinolint megfőzzük, hagyjuk kihűlni, és egyformára szeleteljük. Közben az olajat az ecettel elkeverjük, hozzájuk a hagymát és a fokhagymát, és ízesítjük sóval és 1 kevés borssal. Salátás Tálban a babot a krinolinnal össze- serjak, és ráöntjük a salátalevet. Hűtőszekrényben kb. 20 percig állni hagyjuk, majd még egyszer összekeverjük.', 455, 53),
(6, 'Céklasaláta', 'két személyre: 150 g cékla,1/2 fej vöröshagyma, 1 közepes alma, 2 tk. reszelt torma, 1 ek. ecet, víz, só, őrölt köménymag, Sunipol folyékony mesterséges édesítőszer.', 'Elkészítés: A céklát és az almát megmossuk, meghámozzuk, lereszeljük. A megtisztított hagymát kis kockák rávágjuk. Összekeverjük az alapanyagokat, ráöntjük az ecetet. Sóval, mesterséges édesítőszerrel, őrölt köménymaggal ízesítjük.', 15, 3),
(6, 'Fejes saláta', 'két személyre: 2 fej saláta, kevés bor- vagy almaecet, só, metélőhagyma, kapor, víz, petrezselyemzöld, Sunipol folyékony mesterséges édesítőszer, víz.', 'Elkészítés: A fejes salátát megmossuk, leveleire szedjük, újra megmossuk, szitán lecsöpögtetjük. Salátástálba rakjuk a leveleket, meglocsoljuk borecettel, mesterséges édesítő szerrel elkevert vízzel és meghintjük az apróra vágott zöldfűszerekkel. Rögtön fogyasztjuk. Elhanyagolható az energia- és szénhidráttartalom.', 0, 0),
(6, 'Görög saláta', 'két személyre: 300 g paradicsom, 100 g saláta uborka, 150 g juhsajt, 1 fej vöröshagyma, gerezd fokhagyma, 2 ek. ecet, 1 ek. salátaolaj, őrölt 1 fekete bors, 8-10 szem olajbogyó, só.', 'Elkészítés: A megmosott paradicsomokat négyfelé vágjuk. A meghámozott uborkát hosszában kettévágjuk, a magokat eltávolítjuk, és az uborkát vastagabb szeletekre vágjuk. A megtisztított hagymát vékonyan felszeleteljük, a juhsajtot egyforma kockákra, a fokhagymát apróra vágjuk. Az ecetet a salátaolajjal elkeverjük, ízesítjük a sóval, a borssal és a fokhagymával. Egy mély salátástálban a paradicsomot, az uborkát és a juhsajtot összekeverjük, tetejére tesszük a hagymakarikákat, és ráöntjük a salátaöntetet.', 390, 0),
(6, 'Karfiolsaláta', 'két személyre: 300 g karfiol, 4 szem retek, kevés citromlé, só, bors, reszelt szerecsendió, 1 tk. mustár, 1 tk. olaj, fél dl 12%-os tejföl, 1 dl joghurt.', 'Elkészítés: A karfiolt rózsáira szedve megfőzzük, hagyjuk halni, majd olajból, citromléből, mustárból salátalevet készítünk, ízesítjük, sóval, borssal, szerecsendióval. összekeverjük a tejfölt a joghurttal. A keveréket a karfiolra ontjuk. Tetejét retek karikákkal díszítjük.', 110, 3),
(6, 'Káposztasaláta', 'két személyre: 400 g fejes káposzta, 1 fej vöröshagyma, 2 tk, mustár, 2 tk. borecet, 2 tk, olaj, víz, só, Polisweet folyékony mesterséges édesítőszer.', 'Elkészítés: A jól megmosott káposztát vékony csíkokra szeleteljük. Enyhén megsózzuk, fél órára állni hagyjuk. Nem nyomjuk ki a levét. Megreszeljük a megtisztított hagymát. Mustárból, ecetből, olajból, mesterséges édesítőszerből salátalevet készítünk. Beletesszük a hagymát, és rá öntjük a káposztát. Kb. 1 óráig állni hagyjuk.', 45, 0),
(6, 'Kefires fejes saláta', 'Hozzávalók két személyre: 2 fej saláta, 1 gerezd fokhagyma, Polisweet folyékony mesterséges édesítőszer, 1 ek. citromlé, 2 tk, olaj. 1 dl kefir, 1 tk. mustár, fél csomó petrezselyemzöld, 1 csomó metélőhagyma.', 'Elkészítés: A jól megmosott salátát szétszedjük kisebb da- rabokra. A salátás tálban keverjük ki az öntetet. A pépesített fokhagymához adjuk hozzá a citrom levét, az olajat, a mustárt, a folyékony mesterséges édesítőszert, a kefirt, végül az apróra vágott zöld fűszernövényeket. Lazán keverjük hozzá a salátaleveleket.', 77, 3),
(6, 'Kukoricasaláta', 'Hozzávalók két személyre: 280 g kukorica (konzerv), 150 g savanyú uborka, 2 db közepes paradicsom, 3 szál újhagyma, 1 ek. citromlé, 2 tk, olaj, 2 tk. mustár. 2 ek. víz, só, őrölt fehér bors.', 'Elkészítés: A lecsöpögtetett kukoricát az apró kockára vágott uborkával és paradicsommal egy tálban összekeverjük és megsózzuk. A citromléből, olajból, mustárból őrölt fehér borssal ízesítve salátaöntetet készítünk, és ráöntjük az alapanyagokra. Hagyjuk az ízeket összeérni.', 223, 33),
(6, 'Kukoricás fejes saláta', 'két személyre: 2 fej saláta, 100 g kukorica, 2 db paradicsom, 100 g trappista sajt, 2 dl kefir, kevés citromlé, 2 tk. olaj, Polisett mesterséges édesítőszer, só.', 'Elkészítés: A jól megmosott, csíkokra vágott salátához hozzáadjuk a kukoricát, a kis kockákra vágott paradicsomot és sajtot. A salátás tálban jól kikeverjük a citrom le- vét, az olajat és a kefirt, ízesítjük sóval, mesterséges sítőszerrel, majd hozzákeverjük a salátaalapot.', 345, 18),
(6, 'Kukoricás kínai kel saláta', 'két személyre: 200 g konzerv, vagy főtt kukorica, 150 g kínai kel, 150 g póréhagyma, 2 ek. ecet, 2 ek. víz, 1 ek. salátaolaj, apróra vágott petrezselyem zöld.', 'Elkészítés: A kínai kelt és a póréhagymát megtisztítjuk és megmossuk, a kínai kelt vékony csíkokra, a póréhagymát 2 cm vastag karikákra vágjuk. A leszúrt konzerv kukoricát hagyjuk lecsöpögni. Az így előkészített saláta alapanyagokra ráöntjük az ecetet, a vizet a salátaolajat, és az egészet jól összekeverjük. Tálaláskor meghintjük petrezselyemzölddel.', 173, 24),
(6, 'Olasz saláta', 'két személyre: 1/2 fejes saláta, 200 g brokkoli, 200 g sárgarépa, 60 g apróra tördelt makarónitészta megfőzve, 100 g hideg sülthús, 1 ek. borecet, 1 ek. salátaolaj, olasz fűszerkeverék vagy só és őrölt fehér bors.', 'Elkészítés: A megmosott salátaleveleket apróra tépkedjük, a brokkolit rózsáira szedjük, a megtisztított sárgare pát karikákra, a hideg sültet csíkokra vágjuk. A borecetet 2 salátaolajjal, esetleg kevés vízzel összekeverjük, sóval és borssal ízesítjük. A saláta alapanyagokat egy tálban a kihűlt főtt tésztával és az öntettel összekeverjük. Fogyaszthatjuk a salátát néhány szem olajbogyóval együtt tálalva is.', 295, 30),
(6, 'Paradicsomsaláta', 'Hozzávalók két személyre: 400 g paradicsom, 1/2 fej vöröshagyma, 1 csomó kapor, 1 mk. bazsalikom, petrezselyemzöld, 2 tk. olaj, 1 ek. borecet, víz, só, fehér bors.', '\r\nElkészítés: A salátástálban elkeverjük az olajat az apróra vágott hagymakockákkal, a petrezselyemzölddel, a kaporral, valamint a borecettel, a vízzel, és még ízesítjük sóval. Legvégül beleforgatjuk a vékony karikákra vágott paradicsomokat.', 45, 0),
(6, 'Reteksaláta tojással', 'két személyre: 200 g hónapos retek, 1 csomó újhagyma, 2 dl joghurt, 2 főtt tojás, só.', 'Elkészítés: Salátástálba öntjük a joghurtot, sózzuk. Majd beleforgatjuk a zöldjével együtt apróra vágott újhagymát és a héjastól felszeletelt retket. Az egyik főtt tojást apró kockákra vágva hozzáadjuk. A megmaradt főtt tojást szeleteljük fel, ezzel fedjük be a salátát.', 135, 5),
(6, 'Sajtos gombasaláta', 'két személyre: 100 g óvári sajt, 300 g konzerv gomba, petrezselyemzöld, 1 ek. borecet 1/2 dl joghurt, só, bors, víz.', 'Díszítés: A leszűrt gombát szeletekre, a sajtot kis kockákra vágjuk, megszórjuk az apróra vágott petrezselyem- dél, és jól összekeverjük. Az ecetből, joghurtból, konzervből öntetet készítünk, sóval, borssal ízesítjük, elkeverjük a saláta-alapanyagokkal.', 200, 2),
(6, 'Sajtos körtesaláta', 'Hozzávalók két személyre: 150 g Túra sajt, 2 közepes körte, 1 zöldpaprika, 1 mk. mustár, fél citrom leve, só, bors, gyömbér, folyékony mesterséges édesítőszer.', 'Elkészítés: A megmosott körtét és zöldpaprikát, valamint 1 sajtot vékony csíkokra vágjuk. Citromléből, mustárból, mesterséges édesítőszerrel salátalevet készítünk, ízesítjük sóval, borssal, gyömbérrel, majd ráöntjük a saláta-alap anyagokat.', 190, 13),
(6, 'Sajtos virslisaláta', 'Hozzávalók két személyre: 150 g főtt virsli, g zsírszegény sajt, 150 g savanyú uborka,100 g vöröshagyma, 2 ek. ecet, 1 ek. saláta olaj, só, őrölt fehér bors, folyékony mesterséges édesítőszer, apróra vágott metélőhagyma.', 'Elkészítés: A főtt virslit karikára, a sajtot kockára, a hagymát vékony karikákra vágjuk és a savanyú uborkát felszeleteljük. A salátástálban összekevert alapanyagokra ráöntjük az ecetből, olajból, sóból, őrölt borsbólés ízlés szerint a folyékony édesítőszerrel édesített saláta- metélőhagymával. öntetet.', 310, 0),
(6, 'Sárgarépa-alma saláta', 'két személyre: 200 g sárgarépa. 200 g alma, fél citrom leve, folyékony mesterséges édesítőszer.', 'Elkészítés: A megtisztított sárgarépát és az almát héjastól finomra reszeljük. Ráöntjük a kevés vízben elkevert mesterséges édesítőszert és a fél citrom kicsavart levét, jót elkeverjük.', 65, 15),
(6, 'Sárgarépa-saláta', 'két személyre: 400 g sárgarépa, fél citrom leve, folyékony mesterséges édesítőszer.', 'Elkészítés: A megtisztított sárgarépát finomra szeleteljük. ízesítjük az édesítővel és a citrom levével.', 70, 16),
(6, 'Színes burgonyasaláta', 'két személyre: 300 g burgonya, 100 g főtt sonka vagy maradék hideg sült, 100 g konzerv kukorica, 100 g zöldbab, 100 g kígyóuborka,100 g vöröshagyma, 50 g piros zöldpaprika, 2 ek. borecet, 1 tk. saláta olaj, frissen őrölt fekete bors, só, ízlés szerint friss, apróra vágott petrezselyemzöld, kapor és metélőhagyma.', 'Elkészítés: A burgonyát héjastól megfőzzük, majd a héját levesszük és egyforma, nem túl kicsi kockákra vágjuk. Közben a megtisztított és kb. 2 cm-es darabokra vágott zöldbabot kevés vízben ropogósra főzhetjük. (Természetesen használhatunk konzerv zöldbabot is.) A konzerv kukoricát lecsöpögtetjük, a megmosott uborkát felszeleteljük, a meg tisztított hagymát vékony karikákra, a piros zöldpaprikát apró kockákra vágjuk. Az ecetből, olajból, egész apróra vágott zöldfűszerekkel, sóval és borssal ízesítve pikáns ön tetet készítünk. Az alapanyagokat egy salátástálban összekeverjük, ráöntjük a salátalevet, és egyszer összekeverjük, tálalás előtt legalább 10 percig állni hagyjuk.', 309, 42),
(6, 'Tarka fejes káposzta', 'két személyre: 200 g fejes káposzta, 2 közepes paradicsom, 2 zöldpaprika, 1/2 csomó retek, néhány szál metélőhagyma és petrezselyemzöld, 2 ek. fűszeres ecet, 1 tk. mustár, 2 ek. víz, 1 ek. saláta olaj, só, őrölt fehér bors.', 'Elkészítés: A megtisztított fejes káposzta torzsáját eltávolítjuk és vékony csíkokra vágjuk. A káposztát lobogó sós vízben 1-2 percig előfőzzük, leszűrjük, lecsöpögtetjük és hagyjuk kihűlni. A megtisztított paradicsomot gerezdek Te, a zöldpaprikát csíkokra, a retket vékony szeletekre Vágjuk. Az ecetből, mustárból, vízből, készítünk, ízesítve a sóval, a fehér borssal, az apróra vágott petrezselyemzölddel és metélőhagymával, salátaöntetet. Az alapanya- gokat és a salátaöntetet egy tálban összekeverjük és kb. 1 órán át állni hagyjuk.', 45, 0),
(6, 'Tavaszi vegyes saláta', 'Hozzávalók két személyre: 1/2 fejes saláta, 200 g karfiol, 200 g sárgarépa, 100 g kígyóuborka, 100 g főtt zöldborsó, 1 csomó újhagyma, apróra vágott petrezselyemzöld, 2 ek. borecet, 2 ek salátaolaj, 2 tk, mustár, só, őrölt fekete bors, ha van, apróra vágott friss bazsalikom.', 'Elkészítés: A megmosott, lecsöpögtetett fejes salátát csikókra vágjuk. A rózsáira szedett karfiolt jól megmossuk. A megtisztított és megmosott sárgarépát, újhagymát és az uborkát karikákra szeleteljük. Egy salátástálba először a salátát terítjük szét, aztán rátesszük a karfiolt. Tetejére szépen elhelyezzük a sárgarépát, az újhagymát és az uborkát. Az ecetet, az olajat és a mustárt összekeverjük, ízesítjük sóval és borssal, és egyenletesen a salátára öntjük Tetejét szórjuk meg petrezselyemzölddel és bazsalikommal.', 170, 15),
(6, 'Tonhalsaláta', 'két személyre: 200 g mélyhűtött tonhal, 200 g mélyhűtött zöldbab, 2 közepes paradicsom, 2 ek. borecet, 1 ek. saláta olaj, só, borsikafű, 1/2 citrom, 1-2 babérlevél, néhány szem egész fekete bors, 50 g vöröshagyma.', 'Elkészítés: A citromkarikákkal, babérlevéllel, egész borssal, hagymával ízesített enyhén sós páclében a tonhalat -8 percig főzzük, kiszedjük, lecsöpögtetjük, csikókra vágjuk és hagyjuk kihűlni. Közben a zöldbabot is 5-10 percig ropogósra főzhetjük. A megtisztított, megmosott paradicsomot egyforma gerezdekre vágjuk. Összekeverjük az olajat, az ecetet, ízesítjük sóval és borsikafűvel. A paradicsomot a zöldbabbal egy salátás tálban összekeverjük, tetejére tesszük a tonhaldarabkákat és ráöntjük a salátaöntetet.', 200, 0);
INSERT INTO `recipe` (`id`, `name`, `ingredient`, `description`, `energy`, `carbohydrate`) VALUES
(6, 'Uborkasaláta', 'két személyre: 300 g kígyó uborka, 1 csomó újhagyma, 2 tk. borecet, só, fehér bors, folyékony mesterséges édesítőszer.', 'Elkészítés: A jól megmosott uborkát héjával együtt vékony szeletekre gyaluljuk. A megtisztított hagymát zöldjével együtt karikára vágjuk. Összekeverjük az uborkával, sózzuk. Az ecetből, a vízből, a folyékony mesterséges édesítőszerrel salátalevet készítünk. Megborsozzuk, majd ráöntjük az uborkasalátára. Elhanyagolható az energia- és szénhidráttartalom.', 0, 0),
(6, 'Uborkasaláta joghurttal', 'két személyre: 300 g kígyó uborka, 1 gerezd fokhagyma, 5 ek. reszelt juhsajt, 2 dl joghurt, só, bors.', 'Elkészítés: Az uborkát héjastól kis kockákra vágjuk. A fokhagymát peresítjük, és elkeverjük a joghurttal. Hoz adjuk a juhsajtot és a keveréket ráöntjük az uborkára. Sóval, borssal ízesítjük.', 260, 7),
(6, 'Uborkasaláta tejföllel', 'két személyre: 300 g uborka, 1 dl 12%-05 tejföl, 1 csomó metélőhagyma, 1 gerezd fokhagyma, só.', 'Elkészítés: A jól megmosott uborkát felszeleteljük, ha kell, a héját levágjuk. Egy salátástálban a tejfölt összekeverjük az apróra vágott metélőhagymával és a fokhagymával. Beleforgatjuk az uborkát, majd fél óráig állni hagyjuk.', 70, 2),
(6, 'Vegyes őszi saláta', 'két személyre: 1/2 fej vöröshagyma, 150 g feketeretek, 100 g sárgarépa, 100 g alma, 150 g savanyú káposzta, 1 ek. borecet, köménymag, 1 dl joghurt, só.', 'Elkészítés: A megtisztított hagymát kockára vágjuk, az almát a sárgarépával és a retekkel együtt durva reszelőn le reszeljük. Hozzáadjuk a káposztát, és az egészet összekeverjük. A joghurtban elkeverjük az ecetet, a 2 ek. vizet és a köménymagot. Sózzuk, és a saláta öntettel leöntjük az alapanyagokat.', 64, 10),
(6, 'Vegyes tavaszi saláta', 'két személyre: 1 marék parajlevél, 150 g kígyóuborka, 150 g retek, 200 g sárgarépa, só, 2közepes paradicsom, 1 zöldpaprika, fél citrom, folyékony mesterséges édesítőszer.', 'Elkészítés: A megmosott parajleveleket csíkokra, az uborkát, retket karikára, a zöldpaprikát csíkokra, a paradicsomot négy részre vágjuk. A megtisztított sárgarépát megreszeljük. A zöldségféléket salátástálra tesszük. A citromlével elkeverjük az édesítőszert, sózzuk, és a salátára öntjük.', 35, 8),
(6, 'Virslis zöldbabsaláta', 'két személyre: 200 g virsli, 200 g zöldbab, 200 g konzerv vagy főtt kukorica, 1 db paradicsom, 2 tk. mustár, 2 tk. ecet, 2 tk. salátaolaj, só, borsikafű, néhány csepp folyékony édesítőszer, fejes saláta levél, 1 ek. apróra vágott metélőhagyma.', 'Elkészítés: Az egyforma (kb. 2 cm-es) darabokra vágottzöldbabot kb. 15 percig főzzük, leszűrjük és lecsöpögtetjük. A virslit is megfőzzük, és egyforma karikákra vágjuk. (Készíthetjük a salátát apró kockákra vágott párizsiból is.) A kukoricát lecsöpögtetjük, a megmosott paradicsomot kis kockákra vágjuk. Készítsük el az öntetet, keverjük össze az ecetet, az olajat, a mustárt, és ízesítsük sóval. és öntsük rá a salátaöntetet. édesítőszerrel. Keverjük össze a zöldségféléket a virslivel és öntsük rá a saláta öntetet. Néhányszor keverjük még össze. Megmosott, lecsöpögtetett fejes saláta levelekre tá- találjuk a salátát, és a tetejét bőven szórjuk meg metélő- hagymával.', 404, 24),
(6, 'Vöröskáposzta-saláta juhsajttal', 'két személyre: 400 g fejes káposzta (egy fél vörös káposzta), 150 g juhsajt, 3 ek. borecet, ek. salátaolaj, őrölt fekete bors, folyékony 2 édesítő szer, 30 g tisztított tökmag, só.', 'Elkészítés: A fejes káposzta külső leveleit eltávolítjuk és durvára reszeljük. Forrásban lévő lobogó sós vízben 1-2 percig előfőzzük, leszűrjük, lecsöpögtetjük. Összekeverjük az ecetet és az olajat, ízesítjük sóval, borssal és folyékony édesítőszerrel. A tökmagot teflonedényben megpirítjuk, a juhsajtot egyforma kockákra vágjuk. Egy salátás tálban a káposztára öntjük az öntetet, és lefedve legalább egy órán át állni hagyjuk. Tálalás előtt hozzákeverjük a juhsajtot és a pirított tökmagot.', 460, 1),
(6, 'Vörös káposzta körtével', 'két személyre: 200 g vörös káposzta, 300 g körte, 1 ek. borecet, víz, só, bors, gyömbér, folyékony mesterséges édesítőszer.', 'Elkészítés: A megmosott káposztát vékony csíkokra vágjuk, megsózzuk, és állni hagyjuk. Vízből, borecetből a mesterséges édesítőszerrel öntetet keverünk egy salátás tálban. Ebbe reszeljük bele a körtéket. Hozzáadjuk a káposztát, sóval, borssal, gyömbérrel ízesítjük. Jól átkeverjük.', 75, 18),
(6, 'Zöldbabsaláta', 'két személyre: 300 g főtt vagy konzerv zöldbab, 2 dl 12%-os tejföl, 1 ek. mustár, 2 tk, borecet, petrezselyemzöld, só, bors, folyékony mesterséges édesítőszer.', 'Elkészítés: A leszűrt zöldbabot salátástálba tesszük. Mustárból, tejfölből, ecetből öntetet készítünk, mesterséges édesítőszerrel, sóval, borssal, apróra vágott zöldpetrezselyemmel ízesítjük. Ráöntjük a zöldbabra és a salátát 1 órát állni hagyjuk.', 135, 4),
(6, 'Zöldborsósaláta', 'Hozzávalók két személyre: 300 g zsenge zöldborsó, I csomó újhagyma, 1/2 fejes saláta, 1/2 csomó petrezselyemzöld, 2 ek. borecet, 2 ek, víz, 1 ek. saláta olaj, folyékony mesterséges édesítőszer, só, őrölt fekete bors.', 'Elkészítés: A zöldborsót lobogó sós vízben kb. 10 percig főzzük, leszűrjük, és hagyjuk kihűlni. A megtisztított új. hagyma világosabb és sötétebb zöld részét is vékony karikákra vágjuk, az alapanyagokat egy salátástálban összekeverjük. Az ecetből, vízből, olajból, ízesítve a folyékony édesítőszerrel, sóval és őrölt fekete borssal, öntetet készítünk, és ráöntjük a salátára.', 177, 21),
(7, 'Alufóliában sült burgonya', 'két személyre: 400 g burgonya', 'Elkészítés: A burgonyát kefével, váltott vízben alaposan letisztítjuk, majd egyesével alufóliába burkoljuk, és kb. 45 percig sütjük.', 180, 40),
(7, 'Burgonyafánk', 'két személyre: 400 g héjában főtt burgonya, 2 tojássárgája, 1 ek. liszt, só, kb. 2 dl olaj a sütéshez (hogy tudjon lebegni, de kevés olajat vesz fel).', 'Elkészítés: A főtt burgonya héját eltávolítjuk, burgonya nyomón átnyomjuk, megsózzuk, és hagyjuk hűlni. Ha kihűlt, hozzákeverjük a tojások sárgáját és a lisztet. Leg. alább egy órán át állni hagyjuk. Evőkanállal a felhevített olajba kis halmokat szaggatunk, és szép világosra sütjük a burgonyafánkokat. Húsételekhez köretként tálaljuk.', 320, 43),
(7, 'Burgonyapüré', 'két személyre: 400 g burgonya, 1 dl 1,5%-os tej, 2 tk. Delma margarin, só.', 'Elkészítés: A héjában megfőzött, majd megtisztított burgonyát burgonyatörőn átnyomjuk. Hozzáöntjük a tejet és a margarint, habverővel vagy robotgéppel habosra keverjük. Tálalás előtt készítjük el.', 242, 42),
(7, 'Galuska', 'két személyre: 100 g liszt, 1/2 tojás, víz, só.', 'Elkészítés: A lisztet a tojással összekeverjük és annyi vizet adunk hozzá, hogy a tésztát lobogó sós vízbe ki tudjuk szaggatni. Körülbelül 10 percig főzzük, leszűrjük.', 205, 35),
(7, 'Francia burgonya', 'két személyre: 400 g burgonya, 1 dl 12%-os tejföl, 1 tojás, só, szerecsendió, 2 tk. Delma margarin.', 'Elkészítés: A megfőzött, meghámozott burgonyát kari kára vágjuk, margarinnal kikent tűzálló tálba tesszük. A tojássárgáját elkeverjük a tejföllel, a habbá vert tojásfehérjével, belereszelünk kevés szerecsendiót. A burgonyát leöntjük a tejfölös masszával, és előmelegített sütőben addig sütjük, amíg a teteje megpirul.', 310, 42),
(7, 'Karalábé tejföllel', 'két személyre: 4 fej karalábé, 1 ek, olaj, 1 tojássárgája, 1 dl 12%-os tejföl, petrezselyemzöld, só.', 'Elkészítés: A csíkokra vágott karalábét az olajon megpároljuk, megszórjuk apróra vágott petrezselyemzölddel. Lapos tűzálló tálba borítjuk, és ráöntjük a tojássárgájával elkevert tejfölt. A sütőben átforrósítjuk.', 130, 2),
(7, 'Kínai kel sárgarépával', 'két személyre: 400 g kínai kel, 300 g sárgarépa, 1 ek. olaj, bazsalikom, só, őrölt fekete bors.', 'Elkészítés: A megtisztított és megmosott kínai kelt vékonycsíkokra, a sárgarépát karikákra vágjuk. Lábasba öntjük az olajat, felhevítjük, beletesszük a kínai kelt és a sárgarépát, megkevergetve átforrósítjuk, majd kb. 1 1,5 dl vízzel felöntjük, sóval, borssal ízesítjük, és fedő alatt a vizet lassan addig pároljuk, amíg a kínai kel össze nem esik. Ha lehet, a tetejét friss bazsalikommal meghintve tálaljuk.', 105, 12),
(7, 'Párolt zöldségfélék', 'két személyre: 600 g zöldség, 2 tk. olaj, só.', 'Elkészítés: A felhevített olajon a megtisztított és megmosott zöldségféléket átfuttatjuk, kevés folyadék - víz, zöldséglé, csontlé - hozzáadásával fedő alatt puhára pároljuk.', 45, 0),
(7, 'Pirított dara', 'két személyre: 6 ek, búzadara, 2 tk, olaj, só.', 'Elkészítés: A darát forró olajban zsemleszínűre pirítjuk, 1 dl forró vizet lassan hozzáöntünk, és addig pirítjuk tovább, amíg meg nem puhul és morzsás nem marad.', 145, 21),
(7, 'Rizibizi', 'két személyre: 80 g (4 ek.) rizs, 2 tk, olaj, 100 g zöldborsó, petrezselyemzöld, só.', 'Elkészítés: A rizst hagyományos módon elkészítjük. Közben megfőzzük a zöldborsót. Amikor mindkettő kész, összekeverjük, és meghintjük az apróra vágott petrezselyemzölddel.', 225, 39),
(7, 'Rizsköret', 'két személyre: 80 g (4 ek. rizs), 2 tk. olaj, petrezselyemzöld, só.', 'Elkészítés: A megmosott rizst leszűrjük, kevés ideig állni hagyjuk. Az olajat megforrósítjuk, és a rizst 3-4 percig megfuttatjuk benne. Majd kétszer annyi forró vízzel fel öntjük, sózzuk, felforraljuk, és utána lefedve a tűzhely szélére húzzuk. Hagyjuk a rizst megduzzadni. Közben ne kevergessük. Kb. 25-30 perc alatt puhul meg. Apróra vágott petrezselyemzölddel meghintve tálaljuk.', 181, 32),
(7, 'Sárgarépa burgonyával', 'két személyre: 400 g sárgarépa, 200 g burgonya, 10 ek. reszelt óvári sajt, só.', 'Elkészítés: A megtisztított sárgarépát, burgonyát vékony szeletekre vágjuk, összekeverjük, megsózzuk. Jénai tálba kb. 1-2 ujjnyi vizet öntünk, belerakjuk az alapanyagokat, meghintjük a durvára reszelt sajttal. Fedővel letakarva, előmelegített sütőben kb. 30 perc alatt puhára párolódik.', 290, 36),
(7, 'Sárgarépa margarinon párolva', 'két személyre: 400 g sárgarépa, 1 fej vöröshagyma, petrezselyemzöld, 15 g Héra margarin, só.', 'Elkészítés: A megtisztított, szeletekre vágott sárgarépát az apróra vágott hagymával együtt lassú tűzön a margarinnal puhára pároljuk. Tálaláskor szórjuk meg bőségesen petrezselyemzölddel.', 0, 0),
(7, 'Szalmaburgonya', 'két személyre: 400 g burgonya, olaj, só.', 'Elkészítés: A burgonyát megtisztítjuk, vékony csíkokra vágjuk, megmossuk, tiszta konyharuhán megszárogatjuk. Forró, bő olajban szalmasárgára sütjük. Szűrőlapáton le csöpögtetve szedjük ki. Sózzuk.', 220, 40),
(7, 'Vízben főtt sós burgonya', 'két személyre: 400 g burgonya, só, petrezselyemzöld', 'Elkészítés: A meghámozott, kockára vágott, burgonyát sós vízben puhára főzzük, leszűrjük, tálaláskor meghintjük a petrezselyemzölddel.', 180, 40),
(7, 'Zöldborsó karalábéval', 'két személyre: 300 g zöldborsó, 1 fej karalábé, 15 g Héra margarin, 2 lestyánlevél, só.', 'Elkészítés: A kuktában megforrósított margarinra rádobjuk a megtisztított zöldborsót és a csíkokra vágott karalábét, majd a lestyánleveleket is. 1 dl vízzel felöntve kb. 5 percig főzzük.', 175, 21),
(7, 'Zöldborsó margarinon párolva', 'két személyre: 500 g zöldborsó, 15 g Héra margarin, petrezselyemzöld, só.', 'Elkészítés: a kifejezetten zsenge zöldborsót a margarinon megpároljuk, sózzuk, ha kell, kevés vizet öntünk hozzá. Tálaláskor hintjük meg az apróra vágott petrezselyemzölddel.', 280, 35),
(8, 'Alufóliában sült csirke párolt káposztával', 'két személyre: 2 csirkecomb, 500 g fejeskáposzta, só, ecet, folyékony mesterséges édesítőszer, köménymag, bors, 1/2 fej vöröshagyma.', 'Elkészítés: A megmosott csirkecombot besózzuk, meg. hintjük borssal, és alufóliába csomagolva 20 percig sütjük. Majd kinyitjuk a fóliát, és a combot hagyjuk még kb. 10 percig pirulni, hogy ízletesebb legyen. Közben a fejes káposztát vékony szeletekre vágjuk vagy legyaluljuk. Teflon edényben az apróra vágott hagymát megfonnyasztjuk, erre tesszük a káposztát, majd ecettel, mesterséges édesítőszerrel, köménymaggal ízesítjük, kevés vízzel felöntjük. Ne sokat kevergessük, hogy a káposzta ne töredezzen, szálas maradjon.', 110, 0),
(8, 'Borsos tokány', 'két személyre: 300 g főtt marhahús, fej vöröshagyma, 1 paradicsom, só, bors, majoránna, 1 gerezd fokhagyma', 'Elkészítés: A megtisztított hagymát karikára szeleteljük, s teflonedényben 1 dl vízzel pároljuk. Beletesszük a 3 4 cm hosszú, 1 cm széles csíkokra vágott főtt marhahúst, 1 paradicsomot. Ízesítjük sóval, borssal, majoránnával, fokhagymával, majd még 1 dl vizet ráöntve, jól lefedve puhára pároljuk. Leve rövid, sűrű, barna színű legyen.', 180, 0),
(8, 'Brokkolis kölesrizottó', 'két személyre: 60 g köles, 2,5 dl víz vagy zöldséglé, 10 g Héra margarin, 300 g brokkoli, 100 g juhsajt, 50 g vöröshagyma, 100 g paradicsom, őrölt fekete bors, só, 1 db babérlevél, I gerezd fokhagyma, metélőhagyma.', 'Elkészítés: A megtisztított vöröshagymát és fokhagymát apróra vágjuk és a felhevített margarinon megfonnyaszt- megmosott köleshez hozzáadjuk a hagymát, a babérlevelet és a megadott mennyiségű folyadékkal lefedve egyszer felfőzzük, majd kis lángon még kb. 25 percig főzzük. Többször keverjük meg, hogy le ne égjen. Közben a megmosott és rózsáira szedett brokkolit kb. 5-8 percig forró, sós vízben előfőzzük. A juhsajtot kockára, a paradicsomot apró kockákra, a metélőhagymát apróra vágjuk. A brokkolit és a juhsajtot a köleshez keverjük, tetejét megszórjuk a metélőhagymával és az apró paradicsom kockákkal. Tálalás előtt a rizottót grillsütőbe is betehetjük néhány percre. Nem feltétlenül szükséges, hogy a juhsajt megolvadjon.', 340, 20),
(8, 'Haltorta', 'két személyre: 100 g finom liszt, 50 g Graham liszt, 100 g Rama margarin, 200 g félzsíros tehéntúró, 2 db tojás, 400 g mélyhűtött tengeri hal, 300 g mélyhűtött paraj, 1 ek. citromlé, őrölt fekete bors, reszelt szerecsendió, 80 g zsírszegény sajt, só, margarin.', 'Elkészítés: A lisztet a margarinnal, a túróval, a tojással - ízesítve sóval, borssal és szerecsendióval - összegyúrjuk A tészta felét akkorára nyújtjuk, hogy éppen egy kerek, kb. 28 cm átmérőjű sütőforma aljára teríthessük. Előtte vékonyan kenjük ki a formát margarinnal. A mélyhűtött halat és a parajt közben hagyjuk kiolvadni. A jól lecsöpögtetett parajt terítsük szét a tésztán. Hintsük meg borssal. A halat vágjuk kockára, sózzuk meg, csepegtessük rá a citromlevet, és egyenletesen osszuk szét a paraj tetején. A maradék tésztát nyújtsuk megint kerekre és fedjük be vele a tortát. Elő melegített sütőben süssük kb. 40 percig, szórjuk meg a tetejét a reszelt sajttal, és még kb. 5-10 percig hagyjuk a sütőben. A sütőformában még 10 percig hűlhet a tészta. Ezután borítsuk ki a formából. Nyolcfelé vághatjuk.', 294, 15),
(8, 'Kelbimbó egytál', 'Hozzávalók két személyre: 400 g kelbimbó, 300 g burgonya, 200 g főtt sonka (vagy gépsonka), 1 dl 12%-os tejföl, 1 dl 1,5%-os tej, 2 tojás, őrölt fehér bors, őrölt szerecsendió, apróra vágott petrezselyemzöld, só, Delma margarin a sütőforma kikenéséhez.', 'Elkészítés: A megtisztított és megmosott kelbimbót forrásban lévő sós vízben kb. 12-15 percig főzzük, majd leszűrjük. Ugyanebben a vízben főzzük 5 percig a megtisztított és karikákra vágott burgonyát, majd öntsük le róla a vizet. A sonkát vágjuk egyforma kockákra. A tejfölt, a tejet és a felvert tojásokat jól keverjük össze, és ízesítsük fehér borssal, szerecsendióval, sóval. A burgonyaszeleteket margarinnal kikent kerek tűzálló tálba egyenletesen elhelyezzük, és megszórjuk sok vágott petrezselyem zölddel. A burgonyát még sózzuk és borsozzuk. A kelbimbót összekeverjük a sonkával, és a burgonyára rakjuk. A tojásos keveréket egyenletesen ráöntjük. Előmelegített sütőben kb. 40 percig sütjük. Sonka nélkül is elkészíthetjük.', 513, 35),
(8, 'Kelbimbó rétegesen', 'Hozzávalók két személyre: 400 g kelbimbó, 200 g darált sovány sertéshús, 2 ek. reszelt óvári sajt, 1 dl joghurt, majoránna, szerecsendió, só.', 'Elkészítés: A félbevágott kelbimbókat sós vízben, fedő nélkül puhára főzzük és leszűrjük. A darált húst sóval, majoránnával ízesítve teflonban megpároljuk. Rétegesen lerakjuk a kelbimbót és a párolt darált húst. Tetejét leöntjük a joghurtba kevert reszelt sajttal. Forró sütőben addig sütjük, amíg alul-felül színesedni nem kezd.', 355, 12),
(8, 'Kelbimbótorta', 'Hozzávalók két személyre: 130 g finom liszt, 120 g Graham liszt, 20 g élesztő, 100 g Delma margarin, 500 g kelbimbó, 150 g vöröshagyma, 2 db tojás, 2 dl 12%-os tejföl, reszelt szerecsendió, só, margarin a forma kikenéséhez.', 'Elkészítés: Egy tálban a lisztből, a morzsolt élesztőből. a margarindarabkákból késhegynyi sóval és hat evőkanál vízzel tésztát gyúrunk. Meleg helyen lefedve kb. 1/2 órán át állni hagyjuk. Közben a kelbimbót megtisztítjuk, és forrásban lévő sós vízben kb. 3 percig előfőzzük. Leszűrjük, és hagyjuk kihűlni. A megtisztított hagymát vékony karikára vágjuk. A felvert tojásokat összekeverjük a tejföl- lel, és ízesítjük sóval és szerecsendióval. A tésztát kerekre nyújtjuk, és egy kb. 26 cm átmérőjű kerek pizza- vagy tűzálló formába tesszük, a széleket fel húzkodjuk a forma oldalán. A tésztára rakjuk egyenletesen a kelbimbót és a hagymakarikákat A tejfölös tojással leöntjük. Elő melegített sütőben kb. 40-45 percig sütjük. Nyolc Felé vághatjuk.', 514, 45),
(8, 'Lecsó rizzsel, virslivel', 'két személyre: 400 g zöldpaprika, 200 g paradicsom, 50 g rizs, 2 tk. olaj, 1 fej vöröshagyma, 1 pár baromfivirsli, só.', 'Elkészítés: A vöröshagymát karikára vágva az olajon meg fonnyasztjuk. A paradicsomot, zöldpaprikát megtisztítjuk és a hagymához adjuk. Megszórjuk a rizzsel, és annyi vizet öntünk rá, hogy éppen ellepje. Közben a virslit is hozzá adhatjuk karikára szeletelve. Lefedve addig főzzük, amíg a rizs megpuhul.', 183, 20),
(8, 'Paprikás burgonya', 'két személyre: 400 g burgonya, 1 fej vöröshagyma, 1 ek. olaj, 1 mk. fűszerpaprika, só.', 'Elkészítés: A megtisztított burgonyát vékony karikákra vágjuk. A megtisztított hagymát vékonyra szeleteljük, és a felforrósított olajra egyszerre tesszük fel a burgonyával. Fedő alatt kevés vízzel pároljuk. Zsírjára sütjük, megszórjuk a fűszerpaprikával Egy-egy adag: 945 kJ (225 kcal), 40 g szénhidrát. Egy adaghoz 40 g baromfivirslit adva 1280 kJ (305 kcal), 40 g szénhidrát.', 225, 40),
(8, 'Paradicsomos, gombás egytál', 'Hozzávalók két személyre: 300 g gomba, 50 g vöröshagyma, 2 tk, olaj, 200 g paradicsom, 100 g főtt tarja, 1 db tojás, 1 dl 1,5%-os tej, 50 g zsírszegény reszelt sajt, őrölt szerecsendió, őrölt fehér bors, só, petrezselyemzöld a díszítéshez', 'Elkészítés: A megtisztított gombákat négyfelé, a hagymát apróra vágjuk. A felhevített olajon átfuttatjuk a hagymát, hozzáadjuk a gombát, sóval, borssal fűszerezzük, és addig pároljuk, amíg a leve el nem fogy. A paradicsomot gerezdekre, a főtt tarját csíkokra vágjuk. A felvert tojáshoz keverjük a tejet, ízesítjük sóval, borssal és szerecsendióval. A zöldségeket és a sonkát összekeverve, tűzálló tálba tesszük, ráöntjük a tejjel elkevert tojást, és a tetejét meg. hintjük a reszelt sajttal. Előmelegített sütőben kb. 15 percig aranybarnára sütjük. Petrezselyemzölddel díszítve találjuk.', 290, 5),
(8, 'Paradicsomos, gombás egytál', 'Hozzávalók két személyre: 300 g gomba, 50 g vöröshagyma, 2 tk, olaj, 200 g paradicsom, 100 g főtt tarja, 1 db tojás, 1 dl 1,5%-os tej, 50 g zsírszegény reszelt sajt, őrölt szerecsendió, őrölt fehér bors, só, petrezselyemzöld a díszítéshez', 'Elkészítés: A megtisztított gombákat négyfelé, a hagymát apróra vágjuk. A felhevített olajon átfuttatjuk a hagymát, hozzáadjuk a gombát, sóval, borssal fűszerezzük, és addig pároljuk, amíg a leve el nem fogy. A paradicsomot gerezdekre, a főtt tarját csíkokra vágjuk. A felvert tojáshoz keverjük a tejet, ízesítjük sóval, borssal és szerecsendióval. A zöldségeket és a sonkát összekeverve, tűzálló tálba tesszük, ráöntjük a tejjel elkevert tojást, és a tetejét meg. hintjük a reszelt sajttal. Előmelegített sütőben kb. 15 percig aranybarnára sütjük. Petrezselyemzölddel díszítve találjuk.', 290, 5),
(8, 'Parajos puliszka', 'két személyre: 140 g kukoricadara, 500 g paraj, 50 g vöröshagyma, 2 gerezd fokhagyma. 5 g olaj, 1 db tojás, 1 dl joghurt, 10 g szezámmag, 10 g napraforgómag, őrölt bors, szerecsendió, só.', 'Elkészítés: A sóval és a zúzott fokhagymával ízesített 2,5 dl vízben a kukoricadarát állandó kevergetés közben sűrűre főzzük, közben még megszórjuk az őrölt borssal. A parajt alaposan megmossuk, és meg tépkedjük vagy csikókra vágjuk. Az olajon megfonnyasztjuk a hagymát és a maradék zúzott fokhagymát, majd hozzáadjuk a parajt, és lefedve, közepes lángon 3-4 percig pároljuk. Sóval ízesítjük. A levet leöntjük róla. Egy fehér hosszúkás cserépedényt vékonyan kikenünk margarinnal, megszórjuk a szezámmaggal és a kukoricadarát egy vizes evőkanállal beleszaggatjuk. A sütőt kb. 200 °C-ra előmelegítjük. A joghurtban elkeverjük a tojást, ízesítjük a szerecsendióval. A parajhoz keverjük, és az egészet egyenletesen a kukoricadarára öntjük. Tetejét napraforgómaggal meghintjük. Forró sütőben 20-25 percig sütjük. Fejes salátát vagy paprika-paradicsom salátát is fogyaszthatunk hozzá. Szezámmag és napraforgómag nélkül is elkészíthetjük.', 350, 50),
(8, 'Rakott karfiol', 'két személyre: 500 g karfiol, 200 g sovány darált sertéshús, 1 főtt tojás, 1 dl kefir, 1/2 fej vöröshagyma, só, Vegeta.', 'Elkészítés: A megmosott karfiolt rózsáira szedve sós vízben megpároljuk, leszűrjük. A darált húst fűszerezve teflonedényben megpároljuk, rászeleteljük a tojást, meglocsoljuk kefirrel, és sütőben még kb. 10 percig jól átforrósítjuk.', 220, 2),
(8, 'Rakott kelkáposzta', 'két személyre: 500 g fejes káposzta, 200 g darált sovány sertéshús, 1 főtt tojás, só, bors, majoránna, 1/2 dl kefir, 2 tk. Delma margarin.', 'Elkészítés: Hogy a kelkáposzta puffadást ne okozzon, leveleire szedjük, megmossuk, és enyhén sós vízben megpároljuk. A lecsurgatott kelkáposzta felét margarinnal ki kent jénai tál aljára helyezzük. Rászórjuk a tetszés szerint fűszerezett, teflonban párolt darált húst. Ráhelyezzük a vékony karikára szelt főtt tojást, majd lefedjük a meg- maradt káposzta levelekkel. Tetejét meglocsoljuk 2 ek kefirrel, és sütőben jól átsütjük.', 240, 0),
(8, 'Rakott kínai kel', 'Hozzávalók két személyre: 500 g kínai kel, 120 g sovány darált sertéshús, 1 fej vöröshagyma, tojás, 1 db áztatott zsemle, 2 közepes paradicsom, 1 1 dl 12%-os tejföl, 80 g reszelt zsírszegény sajt, apróra vágott petrezselyemzöld, só, őrölt fehér bors, Delma margarin a forma kikenéséhez.', 'Elkészítés: A megtisztított kínai kel leveleket forrásban lévő sós vízben előfőzzük, leszűrjük, és lecsöpögtetjük. A levelek felét egyforma csíkokra vágjuk, és margarinnal kikent tűzálló tálban egyenletesen elosztjuk. A darált húst összekeverjük az apróra vágott hagymával, a tojással, a jól kinyomkodott zsemlével és az apróra vágott paradicsommal, ízesítjük sóval és borssal. A masszát ráterítjük a kínai kelre, és betakarjuk a levelekkel. Tetejére öntjük még az apróra vágott petrezselyemzölddel és reszelt sajttal elkevert tejfölt. Előmelegített sütőben kb. 35-40 percig sütjük.', 442, 21),
(8, 'Sajttal töltött burgonya', 'két személyre: 400 g burgonya, 10 ek. reszelt Túra sajt, 2 dl 12%-os tejföl, szerecsendió, só.', 'Elkészítés: Meghámozzuk a héjában megfőzött burgonyát, kettévágjuk, és belsejét kivájjuk. A kivájt részt összekeverjük a reszelt sajttal, 2 ek. tejföllel, sózzuk. Jénai tálba helyezzük a megtöltött fél burgonyákat, meglocsoljuk a maradék tejföllel, és sütőben az egészet jól átforrósítjuk.', 400, 45),
(8, 'Sült csirke párolt zöldbabbal', 'két személyre: 2 csirkecomb, 500 g zöldbab, só, grill fűszerkeverék.', 'Elkészítés: A csirkecombokat megmossuk, besózzuk, grill fűszer keverékkel megszórjuk, kis időre állni hagyjuk. Tepsibe ujjnyi vastagságban sót teszünk, és ezen piros ropogósra sütjük a csirkét (vagy fóliába csomagolva sütjük). Közben a zöldbabot megtisztítjuk, egyforma darabkákra vágjuk, sózzuk, és teflonban kevés vízben megpároljuk.', 110, 0),
(8, 'Székelygulyás', 'két személyre: 200 g sovány sertéshús, fej vöröshagyma, 1 ek. liszt, 400g savanyú káposzta, só, borsikafű, 1 gerezd fokhagyma, fűszerpaprika, csombor, köménymag, 1 ek, joghurt.', 'Elkészítés: Teflonedényben a megtisztított, apróra vágott hagymát kevés vízzel pároljuk, fűszerpaprikával megintjük, majd hozzáadjuk a kockára vágott, jól megmosott húst. Ízesítjük sóval, fokhagymával, köménymaggal. A húst félig puhára főzzük (ha kell, még vizet önthetünk hozzá). Hozzátesszük a savanyú káposztát, és annyi vízzel öntjük fel, hogy ellepje. Mielőtt kész, behabarjuk, és még egyszer összeforraljuk. Apróra vágott csomborral hintve tálaljuk.', 177, 4),
(8, 'pulykacomb vagy -mell római tálban', 'két személyre: 300 g pulykahús, 1 fej vöröshagyma, 2 zöldpaprika, 2 paradicsom, 100 g zöldborsó, só, bors, majoránna.', 'Elkészítés: A római tálat 1/2 órára hideg vízben fedelével együtt beáztatjuk. A pulykahúst besózzuk, meghintjük majoránnával, és belerakjuk a római tálba. Rászeleteljük a karikára vágott hagymát, zöldpaprikát, paradicsomot, rászórjuk a zöldborsót, majd tetszés szerint ízesítjük sóval, borssal. A római tálat mindig hideg sütőbe téve, a pulykát fokozatosan 40-50 percig pároljuk.', 285, 7),
(8, 'Töltött karalábé', 'Hozzávalók két személyre: 4 fej karalábé, 150 g darált sovány sertéshús, 1 fej vöröshagyma, 1 főtt tojás, 2 ek. zabpehely, 4 ek. reszelt köményes sajt. Piros Arany (vagy 2 ek. joghurt, apróra vágott kapor), mustár, só, majoránna, petrezselyemzöld.', 'Elkészítés: Meghámozzuk a karalábét, a tetejét levágjuk, belsejét kiskanállal kivájjuk. A darált húst a nagyon apjuk. petrezselyemzölddel megpárol- Többször keverjük meg. Ízesítjük fűszerekkel, és be a keménytojást. Hozzáadjuk a zabpelyhet, és lereszeljük jól összedolgozzuk. Megtöltjük a karalábét a masszával, majd a teflon lábasban rakjuk, befedjük a kifúrt karalábé darabokkal, és annyi vizet öntünk rá, hogy éppen el- lepje. Megsózzuk, és fedő alatt lassan főzzük, amíg egészen n puha nem lesz a karalábé (kb. 20 perc). Reszelt sajttal megszórva, Piros Arany csíkokkal díszítve tálaljuk (kaporral és joghurttal ízesítve is fogyaszthatjuk).', 340, 8),
(8, 'Töltött káposzta', 'két személyre: 200 g sovány sertéshús, 1 fej vöröshagyma, 1 ek. rizs, 500 g savanyú káposzta, 2 káposztalevél, só, bors, fűszerpaprika, borsikafű, fél dl 12%-os tejföl.', 'Elkészítés: A darált húshoz hozzáadjuk az apróra vágott hagymát, az 1 ek. előfőzött rizst, ízlés szerint sót, borsot, fűszer paprikát, és jól elkeverjük. Lazíthatjuk a tölteléket 1 marék apróra vágott savanyú káposztával. A tölteléket a két káposztalevélbe csavarjuk. Egy lábasban kevés vízben üvegesre pároljuk a marék apróra vágott hagymát, megszórjuk kevés fűszerpaprikával, és a vágott káposzta felét belehelyezzük. Rárakjuk a tölteléket, és ízesítjük még sóval, borssal, majd befedjük a maradék káposztával. Annyi vizet öntünk rá, hogy elfedje. Közepes lángon főzzük addig, amíg a töltelékek meg nem puhulnak. Tejföl helyett 2 ek. joghurttal díszíthetjük. Mindig forrón tálaljuk.', 220, 9),
(8, 'Töltött padlizsán', 'két személyre: 2 db padlizsán, 150 g sovány sertéshús darálva, 1 főtt tojás, 3 ek, reszelt köményes sajt, petrezselyemzöld, mustár, 1 fej vöröshagyma, só, bors, majoránna.', 'Elkészítés: A padlizsánokat meghámozzuk, félbevágjuk, közepét kikaparjuk, besózzuk, és állni hagyjuk. Kis idő múlva megfordítjuk, és hagyjuk kicsurogni a keserű levét. Az apróra vágott hagymát teflonban üvegesre pároljuk. Hozzáadjuk a darált húst, az apróra vágott petrezselyem zöld felét és tovább pároljuk. Ha kihűlt, hozzáreszeljük a főtt tojást. Ízesítjük mustárral, majoránnával, borssal, és jól összedolgozzuk. A tölteléket a padlizsánokba töltjük. Meghintjük reszelt sajttal, és alufóliába csavargatva kb. 15-20 percig sütjük közepes lángnál. Tetejét a maradék petrezselyemmel meghintve tálaljuk.', 290, 0),
(8, 'Töltött paprika', 'két személyre: 4 db zöldpaprika. 50 g (1 kis doboz) paradicsompüré, 200 g darált sovány sertéshús, 1/2 fej vöröshagyma, só, bors, 1 ek. rizs, folyékony mesterséges édesítőszer.', 'Elkészítés: A darált húst jól összegyúrjuk az apróra vágott hagymával, sóval, borssal, rizzsel. A tölteni való paprikát megmossuk, magját eltávolítjuk, és úgy töltjük meg a töltelékkel, hogy helye legyen a rizsnek a növekedéshez. Közben 1 doboz paradicsompürét annyi vízzel felhígítunk, hogy a belehelyezett paprikákat elfedje. Ízesítjük az egészet még sóval, édesítővel. Addig főzzük, amíg a töltelékek megfőnek és a mártás kellően besűrűsödik.', 215, 13),
(8, 'Töltött tök', 'két személyre: 500 g tök, 200 g darált sovány sertéshús, 5 ek. reszelt óvári sajt, 2 ek, zabpehely, fél dl 1,5%-os tej, petrezselyemzöld, kapor, bazsalikom, Vegeta, 1 gerezd fokhagyma.', 'Elkészítés: A tököt hosszában kettévágva héjától és belsőjétől megtisztítjuk, Vegetával kívül-belül meg dörzsöljük. A darált húshoz hozzáadjuk a kevés tejben beáztatott zabpelyhet, majd ízesítjük apróra vágott petrezselyemzölddel, lereszelt fokhagymával és bazsalikommal. kevés sóval. A két fél tök belsejét megtöltjük a töltelék kel, a tetejét megszórjuk reszelt sajttal, majd alufóliában megsütjük. Tálaláskor apróra vágott, friss kaporral hintjük meg', 266, 8),
(8, 'Zöldborsó sonkával', 'két személyre: 400 g zöldborsó, 150 g gépsonka, 1 ek. olaj, petrezselyemzöld, 2 dl 12%-os tejföl, só, bors.', 'Elkészítés: A megtisztított zöldborsót kuktában, kevés vízzel megfőzzük (kb. 5 perc). A felforrósított olajban megforgatjuk a vékony csíkokra vágott sonkát, hozzáadjuk a leszűrt zöldborsót. Tetszés szerint ízesítve néhány percig kis lángon pároljuk. Tálaláskor ráöntjük a tejfölt és meghintjük petrezselyemzölddel.', 470, 32),
(8, 'Zöldborsós tokány', 'két személyre: 2 db csirkecomb, 1 ek. olaj. 1 fej vöröshagyma, 1 tk, fűszerpaprika, 1 zöldpaprika,1 paradicsom, 500 g zöldborsó, 1 ek. liszt, 1/2 dl 12%-os tejföl, só, bors.', 'Elkészítés: A megtisztított, apró kockákra vágott hagymát olajon üvegesre pároljuk, hozzáadjuk a megmosott, kockára vágott zöldpaprikát és paradicsomot, a fűszerpaprikát és a feldarabolt csirkehúst, megsózzuk, lassú tűzön kb. 30 percig - ha szükséges - kevés víz hozzáadásával pároljuk. Majd hozzáadjuk a kifejtett (vagy mélyhűtött) zöld borsót, és az egészet puhára főzzük. Meghintjük liszttel, és a tejfölt rácsurgatva még egyszer összeforraljuk. Utoljára kevés törött borssal ízesítjük.', 430, 40),
(8, 'Zöldséges csirke', 'Hozzávalók két személyre: 2 db csirkecomb vagy egy csirkemell kettévágva, 300 g burgonya. 200 g sárgarépa, 1 fej karalábé, 1/4 zeller. 100 g petrezselyemgyökér, 100 g kelbimbó,100 g gomba, só, őrölt fehér bors, apróra vágott petrezselyemzöld, Delma margarin a tálkikenéséhez.', 'készítés: A megmosott csirkecombokat konyha papírral törölgetik, sóval, őrölt fehér borssal fűszerezzük. A megtisztított burgonyát vastagabb karikákra, a zöldségeket egyforma hosszúkás darabokra vágjuk. Egy tűzálló tálat kikenünk margarinnal, alulra rakjuk a burgonyaszeleket, és megsózzuk. Ráterítjük a zöldség felét, fűszerezzük sóval és borssal, rárakjuk a csirkecombokat, és befedjük a maradék zöldséggel, utoljára megszórjuk sóval is borssal. Nyáron zöldpaprika-karikákat is rakhatunk be le. Tetejét szórjuk meg sok apróra vágott petrezselyemzölddel. A tálat takarjuk be alufóliával, és 200°C-on elő melegített sütőben kb. 1 órán át süssük.', 313, 38),
(8, 'Zöldséges rizses hús', 'Hozzávalók két személyre: 120 g darált sovány sertéshús, 60 g rizs, 1 csomag mexikói vegyes zöldség, 100 g vöröshagyma, 1 gerezd fokhagyma, só, fűszerpaprika, őrölt bors, 1 ek. olaj, 1 ek. paradicsom püré.', 'Elkészítés: Az apróra vágott hagymát és fokhagymát a felhevített olajon megfonnyasztjuk, hozzákeverjük a fűszer paprikát, a paradicsompürét és a darált húst. Kevergetve átpirítjuk, és annyi vízzel felöntve, hogy éppen ellepje, fedő alatt pároljuk. Külön-külön puhára főzzük a rizst és a vegyes zöldséget, mindkettőt leszűrve és lecsepegtetve hozzákeverjük a kész pörkölthöz. Savanyúsággal fogyaszthatjuk, vagy esetleg reszelt sajttal meghintve tálalhatjuk.', 290, 31),
(8, 'Zöldséges torta', 'Hozzávalók két személyre: 100 g finom liszt, 50 g Graham liszt, 15 g élesztő, 3,5 dl tej, 30 g Delma margarin, 100 g karalábé, 300 g sárgarépa 200 g zsenge zöldborsó, 1 csomó újhagyma, 1/2 csomó petrezselyemzöld, őrölt fehér bors, őrölt szerecsendió só, margarin a sütőforma kikenéséhez.', 'Elkészítés: A liszthez hozzákeverünk kevés sót és a morzsolt élesztődarabkákat. Egy dl tejet megmelegítünk, és a margarinnal együtt a liszthez adjuk. Az egészet fényes tésztává gyúrjuk. A tésztát meleg helyen kb. 30 percig áll ni hagyjuk. Közben a karalábét és a sárgarépát megtisztítjuk, szeletekre vágjuk, és a zöldborsóval együtt lobogó sós vízben kb. 5 percig előfőzzük. A megtisztított újhagymát egyforma darabokra vágjuk. A felvert tojáshoz keverjük a maradék tejet, ízesítjük sóval, borssal, szerecsendióval és apróra vágott petrezselyemzölddel. A tésztát még egyszer átgyúrjuk, és valamivel nagyobbra nyújtjuk a tűzálló tál nagyságánál. A tésztával kibéleljük a tűzálló tálat úgy, hogy a tésztát a szélekig felhúzkodjuk. Szépen elrendezzük a tésztán a zöldségeket, és ráöntjük a tojásos tejet. Előmelegített sütőben kb. 45 percig sütjük, 10 percig hagyjuk hűlni. Ezt a tésztát négyfelé oszthatjuk.', 314, 42),
(9, 'Almatorta', 'Hozzávalók: 150 g liszt, 1 tk. sütőpor, 100 g Rama margarin, 1 tojássárgája, só, 750 g alma, 1/2 db citrom reszelt héja, 2 ek. rum, 40 g darabos mandula, 1 db tojás, folyékony mesterséges édesítőszer, 1 pohár 12%-os tejföl, 1 db tojásfehérje.', 'Elkészítés: A liszthez szórjuk a sütőport, és a margarinnal, a tojássárgájával, a sóval, a reszelt citromhéjjal együtt jól összegyúrjuk. A tésztát legalább 1/2 órán át hűtószekrényben pihentetjük. A meghámozott alma magházát eltávolítjuk, és az almát négyfelé vágjuk. Egy kerek torta- forma alján és oldalán félig szétterítjük kezünkkel a tésztát, és egyenletesen rárakjuk az alma darabokat. A tésztát 220 °C-on előmelegített sütőben kb. 15 percig sütjük. Közben a tojásfehérjéket kemény habbá verjük és könnyeden hozzákeverjük a tejfölt, a tojássárgáját és kb. 10 csepp folyékony édesítőszert. A tésztát a sütőből kiveszszik, és a masszát egyenletesen a tetejére öntjük. Még kb. 15 percig sütjük. Az almatortát 8 szeletre vágjuk.', 278, 23),
(9, 'Almatorta másképpen', 'Hozzávalók két személyre: 150 g liszt, 70 g Rama margarin, 1 tojássárgája, fél sütőpor. Töltelékhez: 600 g alma, 1 tojás és a tésztánál megmaradt 1 tojásfehérje, 1 dl 12%-os tejföl, 1 ek. rum, folyékony mesterséges édesítőszer.', 'Elkészítés: A tészta alapanyagait jól összedolgozzuk, és 1 órát állni hagyjuk. A meghámozott, négyfelé vágott almákat szépen elhelyezzük a tésztán. Előmelegített sütőben 8-10 percig sütjük. Ez alatt az idő alatt elkészítjük a tetejét. A két tojásfehérjét kemény habbá verjük, a tojássárgáját összekeverjük a mesterséges édesítőszerrel, a tejföllel, a rummal, majd legvégül a felvert fehérje habjával. A tésztát kivesszük a sütőből, és a masszát a tetejére öntjük. Újból visszahelyezzük a sütőbe, még kb. 10 percig aranysárgára sütjük. Nyolc szeletet vágunk.', 393, 38),
(9, 'Almás piskóta', '100 g liszt, 3 tojás, 100 g alma, 30 g Glukonon, 10 g margarin, víz.', 'Elkészítés: A tojásokat felütjük, kettéválasztjuk, a fehérjéket hideg helyre helyezzük. A tojássárgáját a Glukononnal jól kikeverjük, állandó keverés mellett hozzáadjuk a lisztet, és ha nagyon sűrű, a három evőkanál vizet. Az almát meghámozzuk, egyenletes vékony szeletekre vágjuk, és hozzákeverjük a masszához. A tojásfehérjékből kemény habot verünk, és óvatosan szintén a masszához keverjük. A tésztát papírral kibélelt sütőlapra vagy egy adagos, kimargarinozott pudingformába tesszük, és 10 percig előmelegített sütőben sütjük. Ha megsült a tészta. konyharuhára kiborítjuk, és egyenletes nagyságú szeleteket vágva felszeleteljük. Egy adag két vastagabb szelet piskóta lesz, amennyiben sütőben, sütőlapon sütöttük.', 722, 108),
(8, 'Almás pite', '400 g liszt, 150 g Delma margarin, 2 db tojás, 1 csomag sütőpor, só, 1000 g alma, 1/2 citrom leve, 1 ek. folyékony mesterséges édesítőszer, őrölt fahéj.', 'Elkészítés: Tálban a lisztet a margarindarabkákkal, a tojásokkal és a sütőporral jól összegyúrjuk, és kb. 1 órán át hűtőszekrényben állni hagyjuk. Közben az almát meghámozzuk, megmossuk és durvára lereszeljük. (Uborkagyalun szeletekre is gyalulhatjuk.) A citromlevet elkeverjük a folyékony édesítőszerrel, ráönjük a reszelt almára, és összekeverjük. A tésztát kettéosztjuk, és két közepes tepsi nagyságú tésztalapot nyújtunk. Az egyiket a tepsi aljára tesszük, rárakjuk az almát, megszórjuk a fahéjjal, és ráborítjuk a másik kinyújtott tésztalapot. A tésztát széleinél lenyomkodjuk, és a tetejét villával megszurkáljuk. Előmelegített sütőben kb. 30 percig sütjük. Kb. 24 db egyforma kockát szelhetünk a süteményből. Készíthetjük 1000 g meggyből is. Egy-egy almás pite: 511 kJ (122 kcal), 15 g szénhidrát. Egy-egy meggyes pite: 544 kJ (130 kcal), 16 g szénhidrát.', 122, 15),
(9, 'Almás rétes', 'két személyre: 1 csomag mélyhűtött réteslap, 300 g alma, 2 ek. zsemlemorzsa, 20 g dió, fahéj, 1 ek. olaj, folyékony mesterséges édesítőszer.', 'Elkészítés: A réteslapot meghintjük morzsával, meglocsoljuk a felhevített olajjal. Megszórjuk a lereszelt almával, az őrölt dióval, a fahéjjal és meglocsoljuk a folyékony édesítőszerrel. Felhajtogatjuk, forró sütőben ropogósra sütjük.', 151, 16),
(9, 'Burgonyás kocka', 'két személyre: 200 g burgonya, 1 ek. olaj, 1/2 fej vöröshagyma, só, 2 tk. fűszerpaprika, 80 g száraz kockatészta.', 'Elkészítés: Az olajon megpirítjuk az apróra vágott hagymát, meghintjük fűszerpaprikával, és belevágjuk apró kockákra a megtisztított burgonyát. Sózzuk, kevés vízzel puhára pároljuk. Közben sós vízben kifőzzük a tésztát, leszűrjük, meleg vízzel leöblítjük, és a burgonya közé keverjük.', 280, 48),
(9, 'Darázsfészek', '400 g liszt, 2 db tojássárgája, 20 g élesztő, 40+100 g Rama margarin, 2,5 dl langyos tej, 150 g dió, folyékony édesítőszer, kb. 1 ek. margarin a tepsi kikenéséhez.', 'Elkészítés: Az élesztőt a 1/2 dl tejben 1 kávéskanál liszttel felfuttatjuk. A felfuttatott élesztővel a lisztből, a tojássárgájából, a 40 g margarinból és a maradék tejből könnyű tésztát gyúrunk, amit hólyagosra kidolgozunk. Langyos helyen letakarva, kb. 3/4 óráig pihentetjük. Lisztezett gyúródeszkán kb. 40 x 40 cm-es nagyságúra nyújtjuk. A 300g margarint az 1 ek. folyékony mesterséges édesítő szerrel keverjük habosra, széles késsel egyenletesen ken- kik rá a tésztára, és szórjuk meg a darált dióval. Csavarjuk jel úgy, mint a rétest, és vágjuk 15 egyforma darabra. A tepsit kenjük ki margarinnal, és a tészta karikákat rak- lyuk egymás mellé. Még kb. 1/2 óráig kelesztjük, majd elő- melegített sütőben 30 percig szép világosra sütjük. Ha megsült, kiborítjuk, tiszta konyharuhával letakarjuk, tálaláskor szedjük szét.', 150, 24),
(9, 'Gesztenyepüré', 'két személyre: 1/2 kg gesztenye, 1 ek. Glukonon, 1 ek. rum, 1/2 rúd vanília.', 'Elkészítés: A fél kg gesztenyét külső héjától megtisztítjuk, kuktában puhára főzzük (úgy, hogy szét ne essen). A vizet lecsurgatjuk, majd (lehetőleg lószőrszitán) melegen áttörjük. Kihűlés után folyékony mesterséges édesítőszerrel ízesítjük, hozzáadunk 1 ek. rumot. 1 ek. Glukonont darabokra vágott 1/2 rúd vaníliát; állni hagyjuk. Fél óra múlva a rudakat eltávolítva, a vaníliával átitatott Glukonont a masszába keverjük. A masszát 50 grammos adagokra osztjuk. Kb. 4-6 adagot kapunk. Átlagos adag tejszínhabra I adag gesztenyepürét burgonyanyomóval rányomunk.', 190, 20),
(9, 'Gyümölcskocsonya', 'két személyre: 100 g meggy, 100g eper, 100 g málna, 100 g ribizli, 1/2 citrom leve és héja, egy fahéjdarabka, ízlés szerint folyékony mesterséges édesítőszer, 2 evőkanál étkezési keményítő vagy rizsliszt.', 'Elkészítés: A gyümölcsöket megtisztítjuk, megmossuk, és lecsöpögtetjük. Körülbelül 4 dl vízhez hozzáadjuk a citrom levét, a héját, a fahéjat, és összefőzzük. A keményítőt 4 evőkanál vízzel simára keverjük, és lassan, állandó kevergetés közben az aromás főzethez melegítjük. Eltávolítjuk a fahéjat és a citromhéját, majd az egészet még egyszer összeforraljuk, és hozzákeverjük a gyümölcsöket. A kocsonyát egy nagyobb kompótos tálba öntjük, és hagyjuk kihűlni. Esetleg tejszínhabbal is tálalhatjuk.', 115, 22),
(9, 'Gyümölcsös tejberizs', 'két személyre: 80 g rizs, 1 dl víz, 2,5 dl tej, kis darab vaníliarúd, folyékony mesterséges édesítőszer, 1 dl tejszín, gyümölcs (például: 100 g kimagozott meggy és 100 g őszibarack mag nélkül).', 'Elkészítés: A megmosott rizst a vízbe feltesszük főni, és amikor a vizet magába szívta, hozzáöntjük a tejet és bele tesszük a vaníliadarabkát. Ha forr, fedjük le, és húzzuk félre, hagyjuk, hogy a saját gőzében megpuhuljon. Ízesítsük a folyékony édesítőszerrel, és ha kehült, keverjük hozzá a felvert tejszínhabot. Az őszibarackot vágjuk egy forma kockákra. Egy üvegtálat vagy más megfelelő formát vizezzünk be, és rakjuk bele a rizst a gyümölcsökkel rétegezve. (Esetleg össze is keverhetjük a rizst a gyümölcsök kel.) Egy-két órát hagyjuk állni. Borítsuk ki egy üvegtál ra. Kettéosztva esetleg még tejszínhabbal és pár szem gyümölccsel díszítve tálalhatjuk. Egy-egy adag: 1394 kJ (332 kcal), 46 g szénhidrát. Egy-egy y adag tejberizs: 1203 kJ (286 kcal), 36 g szénhidrát', 332, 46),
(9, 'Gyümölcstorta', 'két személyre: 5 db tojás, 5 kávéskanál rétesliszt, 1 kávéskanál folyékony édesítőszer, 500 g eper, 1 vaníliaízű pudingpor, 3 dl 1,5%-os tej, folyékony édesítőszer, 1 csomag tortazselatinpor, margarin és liszt a sütőforma kikenéséhez.', 'Elkészítés: A megmosott tojásokat szétválasztjuk. A tojásfehérjéből kemény habot verünk, belekeverjük a tojássárgákat, az édesítőszert és kanalanként a réteslisztet. A margarinnal kikent és liszttel megszórt gyümölcstorta sütőformába öntjük a masszát, és előmelegített sütőben megsütjük. A sütőt ne nyitogassuk. Közben a pudingot a tejjel elkészítjük, ízlés szerint édesítjük, és hűlni hagyjuk. Az eper zöld szárait eltávolítjuk, szűrőkanálba rakjuk, a csap alatt megmossuk, és hagyjuk lecsöpögni, a nagyobb szemeket kettévágjuk. A kihűlt piskótaalapra egyenletesen rákenjük a pudingot, és rárakjuk az eperszemeket. A zselatinport a csomagon olvasható előírás szerint cukor nélkül elkészítjük, és a gyümölcs tetejére öntjük. A tortát nyolcfelé oszthatjuk.', 121, 13),
(9, 'Gyümölcsös túrótorta', 'két személyre: 750 g félzsíros tehéntúró, 3 dl tejszín, 1 narancs leve, folyékony mesterséges édesítőszer, 2 ek. zselatin. Díszítéshez: 2 vagy 3 fajta gyümölcs (kb. 300 g), 2 dl diétás befőtt leve, 1 ek. zselatin.', 'Elkészítés: Egy kerek lábas aljára a cikkekre vágott gyümölcsöket körben elhelyezzük, ráöntjük a felmelegített befőttlében feloldott zselatint, majd hűtőben addig fagyasztjuk, amíg a zselatin megdermed. Közben elkészítjük a túrómasszát. A szitán áttört túróhoz hozzáadjuk a narancslevet, a citromlevet, a reszelt citromhéjat, a tejszínt (ha felvert tejszínhabot teszünk hozzá, krémszerűbb), a kevés vízben felmelegített zselatint, és édesítjük ízlés szerint, Ha a gyümölcsdíszítés megdermedt, ráöntjük a túrómasszát, és ezt is kihűtjük, amig keményre dermed. Ekkor néhány pillanatra forróvízbe mártjuk a lábast, hogy elváljon az oldalától a torta, és óvatosan kiborítjuk egy süteményestálra. Fogyasztásig a hűtőben tartjuk. Díszíthetjük még tejszínhabbal. 12 szeletet tudunk vágni.', 160, 6),
(9, 'Káposztás kocka', 'két személyre: 300 g káposzta, 1 ek. olaj, bors, só, 100 g száraz kockatészta.', 'Elkészítés: A lereszelt káposztát sózzuk, és kb. 20 percig állni hagyjuk, majd a felforrósított olajra dobjuk, és barnára pirítjuk. Törött borssal fűszerezzük. A kifőzött tésztát a káposztával jól elkeverjük. Forrón tálaljuk.', 235, 35),
(9, 'káposztás rétes', 'Hozzávalók két személyre: 1 csomag mélyhűtött rétes, 300g apróra vágott fejes káposzta, 1 tojás, 1 ek. olaj, só, őrölt fekete bors.', 'Elkészítés: A káposztát 1/2 óráig besózva állni hagyjuk, majd jól kinyomkodjuk. Forró olajon megpirítjuk, hozzá adjuk a fekete borsot. Ezzel a töltelékkel töltjük a rétest. Négyfelé vágjuk. 2 szelet egy adag.', 105, 9),
(9, 'Keksztorta', 'két személyre: 250 diabetikus g vajaskeksz, 1 csokoládéízű pudingpor, 100 g margarin, 5 dl 1,5%-os tej, 2 tk. folyékony mesterséges édesítőszer, 1 dl tejszín.', 'Elkészítés: A pudingporból tejjel pudingot készítünk, és még melegen hozzákeverjük a folyékony édesítőszert és a margarint. Hűlni hagyjuk. Egy közepes nagyságú üveg tálat hideg vízzel kiöblítünk, az aljára rakunk egy sor kekszet, megkenjük a krémmel, és ezt addig ismételjük, amíg a keksz tart. A tetején keksz legyen. Folpackkal betakarjuk, és legalább egy éjszakára hűtőszekrényben állni hagyjuk. Másnap a torta szélét meleg vízbe mártott késsel kör bevágjuk, és a tortát egy üvegtálra kiborítjuk. Felvert tej színhabbal díszítve tálaljuk. A tortából összesen nyolc szeletet vághatunk. A maradék tortát (ekkor ne vágjuk fel az egészet szeletekre) mely hűtőben is tárolhatjuk.', 310, 30),
(9, 'Mákos gombóc', 'két személyre: 80 g darált mák, 2 db száraz zsemle, 2,5 dl tej, 2 db tojás, 2 ek. búzadara, 10 g margarin, 30 g zsemlemorzsa.', 'Elkészítés: A darált mákot és a csíkokra vágott zsemlét egy tálban összekeverjük, és ráöntjük a forró tejet. Le takarva kb. 10 percig állni hagyjuk, majd hozzákeverjük a búzadarát és az egész tojásokat. A masszából 6 db egy forma gombócot formázunk és azokat lobogó sós vízben kb. 10 percig főzzük. A gombócokat a margarinon és pirított zsemlemorzsára szedjük és megforgatjuk. Kompóttal együtt vacsorára is tálalhatjuk.', 594, 52),
(9, 'Palacsinta', 'két személyre (6 db palacsintához): 6 ek. liszt, 1/2 tojás, 2 dl 1,5%-os tej, szódavíz, só.', 'Elkészítés: A lisztet a tojással, tejjel jól elkeverjük, ha sűrűnek találjuk adjunk hozzá szódavizet. Sózzuk, és kevés ideig állni hagyjuk.\r\nEgy-egy palacsinta: 250 kJ (60 kcal), 9 g szénhidrát. Ha diétás lekvárral töltjük, 1 db palacsintába 10 g diétás dzsemet tegyünk. Igy egy darab: 290 kJ (70 kcal), 11 g szénhidrát. Ha túróval töltjük: egy adag palacsinta (3 db) tölteléké hez: 100 g félzsíros tehéntúrót, 1/2 tojást, folyékony mesterséges édesítőszert, reszelt citromhéjat, néhány szem mazsolát, 1 ek. 12%-os tejfölt adjunk. Egy darab: 512 kJ (125 kcal), 10 g szénhidrát.', 125, 10),
(9, 'Rácsos linzer', '150 g liszt, 70 g Rama margarin, 50 g Glukonon, 1 tojássárgája, 100 g diétás sárgabarackdzsem', 'Elkészítés: A nyersanyagokból omlós tésztát formálunk. Kétharmad részét elterítve tepsiben, a dzsemmel megkenjük, s a maradék tésztából sodort vékony rudakat ferdén ráfektetjük. Sütés és kihűlés után 10 egyforma szeletre vágjuk. Az összmennyiség energia- és szénhidrátértékét a darabok számával elosztva kapjuk meg egy szelet értékét.', 150, 21),
(9, 'Ribiszkefelfújt', 'két személyre: 2 tojás, 5 ek. zsemlemorzsa, 1 tk. margarin, 250 g ribiszke, folyékony mesterséges édesítőszer', 'Elkészítés: A tojások sárgáját habosra verjük a folyékony mesterséges édesítőszerrel, és hozzákeverjük a zsemlemorzsát. A tojások habbá vert fehérjét óvatosan belekeverjük. A massza felét egy kimargarinozott tűzálló tálba öntjük, rátesszük a jól megmosott, lecsöpögtetett ribiszkét, majd a maradék masszával befedjük. Előmelegített sütőben, közepes lángnál, 1/2 órán át sütjük. Ha kihűlt, négyfelé vágjuk.', 215, 26),
(9, 'Surányi sütemény', '4 tojás, 7 ek. feketekávé, 100 g őrölt dióbél, 150 g liszt, 1 sütőpor, folyékony mesterséges édesítőszer.', 'Elkészítés: Tészta: a tojások sárgáját, az édesítőt és a hideg feketekávét habosra kikeverjük. Hozzáadjuk az őrölt dióbelet, a liszttel elkevert sütőport és legvégül a tojások habbá vert fehérjét. Margarinnal kikent, lisztezett, közepes nagyságú tepsiben úgy sütjük, mint a piskótát. Ha megsült, kiborítjuk és hűlni hagyjuk.\r\nTöltelék: A pudingport a tejjel sűrűre főzzük. Hűlni hagyjuk. Közben habosra keverjük a margarint a mesterséges édesítőszerrel és az 1 tojássárgájával. Hozzákeverjük a ki hűlt pudingmasszát is, és az egészet rákenjük a tészta tetejére. Legvégül a 1/2 tábla csokoládét gőzön felolvasztjuk, 1 ek. tejjel és a tk. margarinnal kihűlésig keverjük. Szép egyenletesen rácsurgatjuk a vaníliakrém tetejére. 20 db egyforma nagyságú kockát vágunk belőle.', 160, 10),
(9, 'Szilvás gombóc', 'Hozzávalók két személyre: 500 g burgonya, 150 g liszt, 1 ek, olaj, 1 db tojássárgája, só, őrölt fahéj, 12 db szilva, 1 tk. Delma margarin, 30 g zsemlemorzsa, 10 g Canderel por.', 'Elkészítés: A héjában megfőtt burgonyát áttörjük, ha ki hűlt, hozzáadjuk az olajat, a tojássárgáját, a lisztet és a sót. Jól összegyúrjuk. Lisztezett gyúródeszkán kb. 1/2 cm vastagságúra nyújtjuk úgy, hogy tizenkét egyforma négyzetet tudjunk belőle vágni. A kimagozott szilvákba késhegynyi fahéjat teszünk, majd a szilvákat a négyzetek közepére rakjuk. A tészták sarkait összefogva, lisztes kézzel gombócokat formázunk. Forrásban lévő vízben addig főzzük, amíg a gombócok feljönnek a víz tetejére. Közben a felhevített margarinon megpirítjuk a zsemlemorzsát és megforgatjuk benne a szilvás gombócokat. Tálaláskor meghintjük édesítőszerrel.\r\nEgy adagnak 3 szilvásgombóc felel meg. A maradékot mely hűtőben tárolhatjuk.', 340, 63),
(9, 'Szilvás pite', 'két személyre: 30 g élesztő, 380 g liszt, 1 db tojás, só, 1 citrom reszelt héja, 60 g Rama margarin, 1,5 dl tej, 1000 g kimagozott szilva, margarin a sütőforma kikenéséhez, Canderel por.', 'Elkészítés: Az élesztőt a tejben 2 teáskanál liszttel felfut tatjuk. Tálba tesszük a lisztet, a margarindarabkákat, és hozzáadjuk a sót és a citrom reszelt héját. A liszt köze pere egy mélyedést nyomunk, beleöntjük a felfuttatott élesztőt, és a kezünkkel vagy egy háztartási robotgéppel fényes tésztát gyúrunk. A tésztát lefedve, meleg helyen kb. 30 percig állni hagyjuk. Állás után a még egyszer átgyúrt tésztát lapos sütőforma nagyságúra nyújtjuk, és a margarinnal kikent formába tesszük. A tésztát egyenletesen befedjük a fél szilvadarabokkal. Még egyszer 20 percig állni hagyjuk, és előmelegített sütőben kb. 35 percig sütjük. A tésztát 16 szeletre oszthatjuk. Tálaláskor szórjuk meg a tetejét Canderel porral.', 129, 27);
INSERT INTO `recipe` (`id`, `name`, `ingredient`, `description`, `energy`, `carbohydrate`) VALUES
(9, 'Sztrapacska', 'Hozzávalók két személyre: 300 g burgonya, 2 ek. liszt. 1/4 tojás, 1 ek. olaj, 60 g juhtúró, 20 g füstölt szalonna.', 'Elkészítés: A burgonyát meghámozzuk, és nyersen finom ra reszeljük. Hozzákeverjük a kissé felhevített olajat, a 1/4 tojást és annyi lisztet, hogy galuskatésztát kapjunk. Lobogó sós vízben galuskákat szaggatunk, és kb. 10 percig főzzük őket. Leszűrjük, leöblítjük. Az apróra vágott szalonnát megpirítjuk, tűzálló tálba tesszük, és ráöntjük a galuskát. Tetejére morzsoljuk a juhtúrót, és előmelegített sütőben kb. 10-15 percig sütjük, amíg a juhtúró \'megpókhálósodik\'.', 331, 37),
(9, 'Töltött őszibarack vaníliakrémmel', 'két személyre: 150 g őszibarack, 10 g darált dió, 20 g diétás meggydzsem, 2 dl 1,5%-os tej. 2 tojás, 1 ek. liszt, 10 g Glukonon, 1/4 vaníliarúd.', 'Elkészítés: Az őszibarackot megmossuk, meghámozzuk, félbevágjuk, a magját eltávolítjuk. A diétás meggydzsemet a darált dióval elkeverjük, ha szükséges, folyékony édesítőszerrel ízesítjük. A fel őszibarackokat megtöltjük, es lefelé fordítva, adagonként mély üvegtálba helyezzük. A tejet a vaníliarúddal felforraljuk, az egész tojásokat a kevés liszttel és glukononnal simára keverjük és óvatosan hozzá melegítjük a tejhez, krém sűrűsége főzzük. Az őszibarackra ráöntjük a meleg krémet, kihatjuk, és hidegen tálaljuk. Ebből a mennyiségből 4 adagot készíthetünk.', 102, 9),
(9, 'Túrófánk', 'két személyre: 200 g félzsíros tehéntúró. 320 g liszt, 60 g margarin, 1 db tojás, 1 db tojássárgája, 20 g élesztő, kevés tej, folyékony mesterséges édesítőszer, csipet só, 2 dl olaj.', 'Elkészítés: Az élesztőt kevés tejben felfuttatjuk, 1 tk. lisztet, csipet sót és néhány csepp folyékony édesítőszert adunk hozzá. Egy tálban a tehéntúrót, a lisztet, a margarint, a tojásokat és a felfuttatott élesztőt jól összedolgozzuk, liszttel a tetejét meghintjük, konyharuhával betakarjuk, és kb. 1/2 órán át állni hagyjuk. Liszttel meghintett gyúródeszkán közepes nagyságú szaggatóval kiszaggatjuk. A tésztát még 1/4 óráig pihentetjük, majd felforrósított olajban kisütjük. Körülbelül 15 db fánkot kapunk.', 150, 17),
(9, 'Túrógombóc', 'két személyre: 250 g félzsíros tehéntúró, 1 dl 12%-os tejföl, 2 tojás, 2 ek. búzadara, ek. zabpehely, folyékony mesterséges édesítőszer, reszelt citromhéj. A kész gombócokra: 5 ek. zsemlemorzsa, 1 tk. Delma margarin.', 'Elkészítés: A túrót a tojássárgájával kikeverjük, hozzáadjuk a mesterséges édesítőszert, majd a búzadarát és a leg végén a habbá vert tojásfehérjét. Kb. 1 óráig állni hagyjuk. Egyenlő nagyságú gombócokat formálunk, amelyeket forró vízben, lassú tűzön 15-20 percig főzünk. A kész gombócokat beleforgatjuk a margarinon megpirított zsemlemorzsába.', 486, 43),
(9, 'Túrós pite', 'két személyre: 250 g liszt, 100 g Delma margarin, 2 db tojássárgája, 20 g élesztő, 2 dl tej, só, 500 g félzsíros tehéntúró, 2 db egész tojás, 2 db tojásfehérje, 2 tk. folyékony édesítőszer, reszelt citromhéja.', 'Elkészítés: Az élesztőt a langyos tejben 1 tk. liszttel felfut tatjuk. A lisztből, a margarinból a tojássárgájával és a fel futtatott élesztővel könnyű, puha tésztát gyúrunk. Két egyforma cipót formázunk belőle. A cipót lisztezett gyúródeszkán közepes tepsi nagyságúra nyújtjuk, és belefektetjük a tepsibe úgy, hogy széleit kb. 1 cm magasan felhúzkodjuk. Az áttört túrót a tojássárgájával, az édesítőszerrel és a citromhéjjal összekeverjük, majd hozzáadjuk a kemény habbá vert 4 tojásfehérjét. A túrótölteléket rákenjük a tésztára, és befedjük a másik cipóból kinyújtott tésztalappal. A tetejét megszurkáljuk villával, és pihenni hagyjuk a tésztát kb. 1/2 óráig. Előmelegített sütőben kb. 40 percig sütjük. Egyforma kockákra vágva 20 db süteményt kaphatunk.', 141, 11),
(9, 'Túros pogácsa', '250 g félzsíros tehéntúró, 250 g liszt, 250 g Rama margarin, só.', 'Elkészítés: Az alapanyagokat jól összedolgozzuk. Kb. egy órán át pihentetjük (előző nap is elkészíthetjük, hűtő szekrényben állni hagyhatjuk). Egyforma nagyságúakra szaggatjuk, majd előmelegített sütőben 20-25 percig sütjük. Az összmennyiség értékeit elosztva a pogácsák számával tudhatjuk meg egy pogácsa energia- és szénhidrát értékét.', 3100, 185),
(9, 'Túrós rétes', 'két személyre: 1 csomag mélyhűtött réteslap, 150 g félzsíros tehéntúró, 1 dl 12%-os tejföl, 1/2 citrom reszelt héja, 1 + 1/4 tojás, folyékony mesterséges édesítőszer, 1 ek, olaj.', 'Elkészítés: A mélyhűtött rétestésztát nedves konyharuhán ki hajtogatjuk, a félzsíros tehén túrót szitán áttörjük. 1 dl tejfölt adunk hozzá, 1/2 citrom reszelt héját, 1 tojássárgáját és a felvert tojáshabot, amelyet folyékony mesterséges édesítőszerrel ízesítünk. A rétestésztát I ek fel forrósított olajjal meglocsoljuk, rátesszük a tölteléket, s a konyharuha szélét felemelve felhajtogatjuk. Olajjal megkent tepsire tesszük. A tetejét felvert tojással meg. kenjük. Forró sütőben pirosra sütjük. Négyfelé vágjuk. 2 szelet egy adag.', 195, 14);
